INSERT INTO `exercises` (`id`, `name`, `gif`, `secondary_muscles`, `instructions`, `lang`, `body_part_id`, `equipment_id`, `target_id`, `created_at`, `updated_at`, `exercise_id`) VALUES
(1, '3/4 sit-up', '1.gif', 'hip flexors,lower back', '[\"Lie flat on your back with your knees bent and feet flat on the ground.\",\"Place your hands behind your head with your elbows pointing outwards.\",\"Engaging your abs, slowly lift your upper body off the ground, curling forward until your torso is at a 45-degree angle.\",\"Pause for a moment at the top, then slowly lower your upper body back down to the starting position.\",\"Repeat for the desired number of repetitions.\"]', 'en', 10, 4, 2, '2024-02-19 19:06:19', '2024-02-19 19:06:22', 1),
(2, '45째 side bend', '2.gif', 'obliques', '[\"Stand with your feet shoulder-width apart and your arms extended straight down by your sides.\",\"Keeping your back straight and your core engaged, slowly bend your torso to one side, lowering your hand towards your knee.\",\"Pause for a moment at the bottom, then slowly return to the starting position.\",\"Repeat on the other side.\",\"Continue alternating sides for the desired number of repetitions.\"]', 'en', 10, 4, 2, '2024-02-19 19:06:22', '2024-02-19 19:06:25', 2),
(3, 'air bike', '3.gif', 'hip flexors', '[\"Lie flat on your back with your hands placed behind your head.\",\"Lift your legs off the ground and bend your knees at a 90-degree angle.\",\"Bring your right elbow towards your left knee while simultaneously straightening your right leg.\",\"Return to the starting position and repeat the movement on the opposite side, bringing your left elbow towards your right knee while straightening your left leg.\",\"Continue alternating sides in a pedaling motion for the desired number of repetitions.\"]', 'en', 10, 4, 2, '2024-02-19 19:06:25', '2024-02-19 19:06:28', 3),
(4, 'all fours squad stretch', '4.gif', 'hamstrings,glutes', '[\"Start on all fours with your hands directly under your shoulders and your knees directly under your hips.\",\"Extend one leg straight back, keeping your knee bent and your foot flexed.\",\"Slowly lower your hips towards the ground, feeling a stretch in your quads.\",\"Hold this position for 20-30 seconds.\",\"Switch legs and repeat the stretch on the other side.\"]', 'en', 9, 4, 14, '2024-02-19 19:06:28', '2024-02-19 19:06:30', 4),
(5, 'alternate heel touchers', '5.gif', 'obliques', '[\"Lie flat on your back with your knees bent and feet flat on the ground.\",\"Extend your arms straight out to the sides, parallel to the ground.\",\"Engaging your abs, lift your shoulders off the ground and reach your right hand towards your right heel.\",\"Return to the starting position and repeat on the left side, reaching your left hand towards your left heel.\",\"Continue alternating sides for the desired number of repetitions.\"]', 'en', 10, 4, 2, '2024-02-19 19:06:30', '2024-02-19 19:06:33', 5),
(6, 'alternate lateral pulldown', '6.gif', 'biceps,rhomboids', '[\"Sit on the cable machine with your back straight and feet flat on the ground.\",\"Grasp the handles with an overhand grip, slightly wider than shoulder-width apart.\",\"Lean back slightly and pull the handles towards your chest, squeezing your shoulder blades together.\",\"Pause for a moment at the peak of the movement, then slowly release the handles back to the starting position.\",\"Repeat for the desired number of repetitions.\"]', 'en', 1, 6, 11, '2024-02-19 19:06:33', '2024-02-19 19:06:36', 6),
(7, 'ankle circles', '7.gif', 'ankle stabilizers', '[\"Sit on the ground with your legs extended in front of you.\",\"Lift one leg off the ground and rotate your ankle in a circular motion.\",\"Perform the desired number of circles in one direction, then switch to the other direction.\",\"Repeat with the other leg.\"]', 'en', 5, 4, 5, '2024-02-19 19:06:36', '2024-02-19 19:06:38', 7),
(8, 'archer pull up', '8.gif', 'biceps,forearms', '[\"Start by hanging from a pull-up bar with an overhand grip, slightly wider than shoulder-width apart.\",\"Engage your core and pull your shoulder blades down and back.\",\"As you pull yourself up, bend one arm and bring your elbow towards your side, while keeping the other arm straight.\",\"Continue pulling until your chin is above the bar and your bent arm is fully flexed.\",\"Lower yourself back down with control, straightening the bent arm and repeating the movement on the other side.\",\"Alternate sides with each repetition.\"]', 'en', 1, 4, 11, '2024-02-19 19:06:38', '2024-02-19 19:06:43', 8),
(9, 'archer push up', '9.gif', 'triceps,shoulders,core', '[\"Start in a push-up position with your hands slightly wider than shoulder-width apart.\",\"Extend one arm straight out to the side, parallel to the ground.\",\"Lower your body by bending your elbows, keeping your back straight and core engaged.\",\"Push back up to the starting position.\",\"Repeat on the other side, extending the opposite arm out to the side.\",\"Continue alternating sides for the desired number of repetitions.\"]', 'en', 3, 4, 13, '2024-02-19 19:06:43', '2024-02-19 19:06:45', 9),
(10, 'arm slingers hanging bent knee legs', '10.gif', 'shoulders,back', '[\"Hang from a pull-up bar with your arms fully extended and your knees bent at a 90-degree angle.\",\"Engage your core and lift your knees towards your chest, bringing them as close to your elbows as possible.\",\"Slowly lower your legs back down to the starting position.\",\"Repeat for the desired number of repetitions.\"]', 'en', 10, 4, 2, '2024-02-19 19:06:45', '2024-02-19 19:06:47', 10),
(11, 'arm slingers hanging straight legs', '11.gif', 'shoulders,back', '[\"Hang from a pull-up bar with your arms fully extended and your legs straight down.\",\"Engage your core and lift your legs up in front of you until they are parallel to the ground.\",\"Hold for a moment at the top, then slowly lower your legs back down to the starting position.\",\"Repeat for the desired number of repetitions.\"]', 'en', 10, 4, 2, '2024-02-19 19:06:47', '2024-02-19 19:06:49', 11),
(12, 'arms apart circular toe touch (male)', '12.gif', 'hamstrings,quadriceps,calves', '[\"Stand with your feet shoulder-width apart and arms extended to the sides.\",\"Keeping your legs straight, bend forward at the waist and reach down towards your toes with your right hand.\",\"As you reach down, simultaneously lift your left leg straight up behind you, maintaining balance.\",\"Return to the starting position and repeat the movement with your left hand reaching towards your toes and your right leg lifting up behind you.\",\"Continue alternating sides for the desired number of repetitions.\"]', 'en', 9, 4, 9, '2024-02-19 19:06:49', '2024-02-19 19:06:53', 12),
(13, 'arms overhead full sit-up (male)', '13.gif', 'hip flexors,lower back', '[\"Lie flat on your back with your knees bent and feet flat on the ground.\",\"Extend your arms overhead, keeping them straight.\",\"Engaging your abs, slowly lift your upper body off the ground, curling forward until your torso is upright.\",\"Pause for a moment at the top, then slowly lower your upper body back down to the starting position.\",\"Repeat for the desired number of repetitions.\"]', 'en', 10, 4, 2, '2024-02-19 19:06:53', '2024-02-19 19:06:56', 13),
(14, 'assisted chest dip (kneeling)', '14.gif', 'triceps,shoulders', '[\"Adjust the machine to your desired height and secure your knees on the pad.\",\"Grasp the handles with your palms facing down and your arms fully extended.\",\"Lower your body by bending your elbows until your upper arms are parallel to the floor.\",\"Pause for a moment, then push yourself back up to the starting position.\",\"Repeat for the desired number of repetitions.\"]', 'en', 3, 12, 13, '2024-02-19 19:06:56', '2024-02-19 19:06:59', 14),
(15, 'assisted hanging knee raise', '15.gif', 'hip flexors', '[\"Hang from a pull-up bar with your arms fully extended and your palms facing away from you.\",\"Engage your core muscles and lift your knees towards your chest, bending at the hips and knees.\",\"Pause for a moment at the top of the movement, squeezing your abs.\",\"Slowly lower your legs back down to the starting position.\",\"Repeat for the desired number of repetitions.\"]', 'en', 10, 1, 2, '2024-02-19 19:06:59', '2024-02-19 19:07:02', 15),
(16, 'assisted hanging knee raise with throw down', '16.gif', 'hip flexors,lower back', '[\"Hang from a pull-up bar with your arms fully extended and your palms facing away from you.\",\"Engage your core and lift your knees towards your chest, keeping your legs together.\",\"Once your knees are at chest level, explosively throw your legs down towards the ground, extending them fully.\",\"Allow your legs to swing back up and repeat the movement for the desired number of repetitions.\"]', 'en', 10, 1, 2, '2024-02-19 19:07:02', '2024-02-19 19:07:05', 16),
(17, 'assisted lying calves stretch', '17.gif', 'hamstrings', '[\"Lie on your back with your legs extended.\",\"Bend one knee and place your foot flat on the ground.\",\"Using your hands or a towel, gently pull your toes towards your body, feeling a stretch in your calf.\",\"Hold the stretch for 20-30 seconds.\",\"Release the stretch and repeat on the other leg.\"]', 'en', 5, 1, 5, '2024-02-19 19:07:05', '2024-02-19 19:07:08', 17),
(18, 'assisted lying glutes stretch', '18.gif', 'hamstrings', '[\"Lie on your back with your legs extended.\",\"Bend your right knee and place your right ankle on your left thigh, just above the knee.\",\"Grasp your left thigh with both hands and gently pull it towards your chest.\",\"Hold the stretch for 20-30 seconds.\",\"Release and repeat on the other side.\"]', 'en', 9, 1, 9, '2024-02-19 19:07:08', '2024-02-19 19:07:12', 18),
(19, 'assisted lying gluteus and piriformis stretch', '19.gif', 'hamstrings', '[\"Lie on your back with your legs extended.\",\"Bend your right knee and place your right ankle on your left thigh, just above the knee.\",\"Grasp your left thigh with both hands and gently pull it towards your chest.\",\"Hold the stretch for 20-30 seconds.\",\"Release the stretch and repeat on the other side.\"]', 'en', 9, 1, 9, '2024-02-19 19:07:12', '2024-02-19 19:07:15', 19),
(20, 'assisted lying leg raise with lateral throw down', '20.gif', 'hip flexors,obliques', '[\"Lie flat on your back with your legs extended and your arms by your sides.\",\"Place your hands under your glutes for support.\",\"Engage your abs and lift your legs off the ground, keeping them straight.\",\"While keeping your legs together, lower them to one side until they are a few inches above the ground.\",\"Pause for a moment, then lift your legs back to the starting position.\",\"Repeat the movement to the other side.\",\"Continue alternating sides for the desired number of repetitions.\"]', 'en', 10, 1, 2, '2024-02-19 19:07:15', '2024-02-19 19:07:17', 20),
(21, 'assisted lying leg raise with throw down', '21.gif', 'hip flexors,quadriceps', '[\"Lie flat on your back with your legs extended and your arms by your sides.\",\"Place your hands under your glutes for support.\",\"Engage your core and lift your legs off the ground, keeping them straight.\",\"Raise your legs until they are perpendicular to the ground.\",\"Lower your legs back down to the starting position.\",\"Simultaneously, throw your legs down towards the ground, keeping them straight.\",\"Raise your legs back up to the starting position.\",\"Repeat for the desired number of repetitions.\"]', 'en', 10, 1, 2, '2024-02-19 19:07:17', '2024-02-19 19:07:20', 21),
(22, 'assisted motion russian twist', '22.gif', 'obliques,lower back', '[\"Sit on the ground with your knees bent and feet flat on the floor.\",\"Hold the medicine ball with both hands in front of your chest.\",\"Lean back slightly, engaging your abs and keeping your back straight.\",\"Slowly twist your torso to the right, bringing the medicine ball towards the right side of your body.\",\"Pause for a moment, then twist your torso to the left, bringing the medicine ball towards the left side of your body.\",\"Continue alternating sides for the desired number of repetitions.\"]', 'en', 10, 13, 2, '2024-02-19 19:07:20', '2024-02-19 19:07:24', 22),
(23, 'assisted parallel close grip pull-up', '23.gif', 'biceps,forearms', '[\"Adjust the machine to your desired weight and height.\",\"Place your hands on the parallel bars with a close grip, palms facing each other.\",\"Hang from the bars with your arms fully extended and your feet off the ground.\",\"Engage your back muscles and pull your body up towards the bars, keeping your elbows close to your body.\",\"Continue pulling until your chin is above the bars.\",\"Pause for a moment at the top, then slowly lower your body back down to the starting position.\",\"Repeat for the desired number of repetitions.\"]', 'en', 1, 12, 11, '2024-02-19 19:07:24', '2024-02-19 19:07:28', 23),
(24, 'assisted prone hamstring', '24.gif', 'glutes,lower back', '[\"Lie face down on a mat or bench with your legs fully extended.\",\"Have a partner or use a resistance band to secure your ankles.\",\"Engage your hamstrings and lift your legs towards your glutes, keeping your knees straight.\",\"Pause for a moment at the top, then slowly lower your legs back down to the starting position.\",\"Repeat for the desired number of repetitions.\"]', 'en', 9, 1, 10, '2024-02-19 19:07:28', '2024-02-19 19:07:32', 24),
(25, 'assisted prone lying quads stretch', '25.gif', 'hamstrings,glutes', '[\"Lie face down on the ground with your legs extended.\",\"Bend your left knee and reach back with your left hand to grab your left foot or ankle.\",\"Gently pull your left foot towards your glutes, feeling a stretch in your left quad.\",\"Hold the stretch for 20-30 seconds, then release.\",\"Repeat with your right leg.\"]', 'en', 9, 1, 14, '2024-02-19 19:07:32', '2024-02-19 19:07:34', 25),
(26, 'assisted prone rectus femoris stretch', '26.gif', 'quadriceps', '[\"Lie face down on the ground with your legs straight.\",\"Bend your right knee and reach back with your right hand to grab your right foot or ankle.\",\"Gently pull your right foot or ankle towards your glutes, feeling a stretch in the front of your right thigh.\",\"Hold the stretch for 20-30 seconds.\",\"Release and repeat on the other side.\"]', 'en', 10, 1, 2, '2024-02-19 19:07:34', '2024-02-19 19:07:36', 26),
(27, 'assisted pull-up', '27.gif', 'biceps,forearms', '[\"Adjust the machine to your desired weight and height settings.\",\"Grasp the handles with an overhand grip, slightly wider than shoulder-width apart.\",\"Hang with your arms fully extended and your feet off the ground.\",\"Engage your back muscles and pull your body up towards the handles, keeping your elbows close to your body.\",\"Continue pulling until your chin is above the handles.\",\"Pause for a moment at the top, then slowly lower your body back down to the starting position.\",\"Repeat for the desired number of repetitions.\"]', 'en', 1, 12, 11, '2024-02-19 19:07:36', '2024-02-19 19:07:40', 27),
(28, 'assisted seated pectoralis major stretch with stability ball', '28.gif', 'shoulders,triceps', '[\"Sit on a stability ball with your feet flat on the ground and your back straight.\",\"Hold a stability ball with both hands and extend your arms straight out in front of you.\",\"Slowly lower the stability ball towards your chest, feeling a stretch in your pectoral muscles.\",\"Hold the stretch for a few seconds, then slowly return to the starting position.\",\"Repeat for the desired number of repetitions.\"]', 'en', 3, 1, 13, '2024-02-19 19:07:40', '2024-02-19 19:07:42', 28),
(29, 'assisted side lying adductor stretch', '29.gif', 'hamstrings,glutes', '[\"Lie on your side with your legs straight and stacked on top of each other.\",\"Bend your bottom leg slightly for stability.\",\"Place your top foot on a stable surface, such as a bench or step.\",\"Keeping your top leg straight, slowly lower it towards the ground, feeling a stretch in your inner thigh.\",\"Hold the stretch for 20-30 seconds.\",\"Return to the starting position and repeat on the other side.\"]', 'en', 9, 1, 3, '2024-02-19 19:07:42', '2024-02-19 19:07:45', 29),
(30, 'assisted sit-up', '30.gif', 'hip flexors', '[\"Sit on the edge of a bench or have someone hold your feet down.\",\"Lie flat on your back with your knees bent and feet flat on the ground.\",\"Place your hands behind your head with your elbows pointing outwards.\",\"Engaging your abs, slowly lift your upper body off the ground, curling forward until your torso is at a 45-degree angle.\",\"Pause for a moment at the top, then slowly lower your upper body back down to the starting position.\",\"Repeat for the desired number of repetitions.\"]', 'en', 10, 1, 2, '2024-02-19 19:07:45', '2024-02-19 19:07:47', 30),
(31, 'assisted standing chin-up', '31.gif', 'biceps,forearms', '[\"Adjust the machine to your desired assistance level.\",\"Stand on the foot platform and grip the handles with an overhand grip, slightly wider than shoulder-width apart.\",\"Keep your chest up and shoulders back, engage your core, and slightly bend your knees.\",\"Pull your body up by flexing your elbows and driving your elbows down towards your sides.\",\"Continue pulling until your chin is above the bar.\",\"Pause for a moment at the top, then slowly lower your body back down to the starting position.\",\"Repeat for the desired number of repetitions.\"]', 'en', 1, 12, 11, '2024-02-19 19:07:47', '2024-02-19 19:07:51', 31),
(32, 'assisted standing pull-up', '32.gif', 'biceps,forearms', '[\"Adjust the machine to your desired weight and height settings.\",\"Stand facing the machine with your feet shoulder-width apart.\",\"Grasp the handles with an overhand grip, slightly wider than shoulder-width apart.\",\"Engage your lats and biceps, and pull yourself up towards the handles.\",\"Pause for a moment at the top, squeezing your back muscles.\",\"Slowly lower yourself back down to the starting position.\",\"Repeat for the desired number of repetitions.\"]', 'en', 1, 12, 11, '2024-02-19 19:07:51', '2024-02-19 19:07:53', 32),
(33, 'assisted standing triceps extension (with towel)', '33.gif', 'shoulders', '[\"Stand with your feet shoulder-width apart and hold a towel with both hands behind your head.\",\"Keep your elbows close to your ears and your upper arms stationary.\",\"Slowly extend your forearms upward, squeezing your triceps at the top.\",\"Pause for a moment, then slowly lower the towel back down to the starting position.\",\"Repeat for the desired number of repetitions.\"]', 'en', 8, 1, 18, '2024-02-19 19:07:53', '2024-02-19 19:07:57', 33),
(34, 'assisted triceps dip (kneeling)', '34.gif', 'chest,shoulders', '[\"Adjust the machine to your desired weight and height.\",\"Kneel down on the pad facing the machine, with your hands gripping the handles.\",\"Lower your body by bending your elbows, keeping your back straight and close to the machine.\",\"Pause for a moment at the bottom, then push yourself back up to the starting position.\",\"Repeat for the desired number of repetitions.\"]', 'en', 8, 12, 18, '2024-02-19 19:07:57', '2024-02-19 19:08:00', 34),
(35, 'assisted wide-grip chest dip (kneeling)', '35.gif', 'triceps,shoulders', '[\"Adjust the machine to your desired height and secure your knees on the pad.\",\"Grasp the handles with a wide grip and keep your elbows slightly bent.\",\"Lower your body by bending your elbows until your upper arms are parallel to the floor.\",\"Push yourself back up to the starting position by extending your arms.\",\"Repeat for the desired number of repetitions.\"]', 'en', 3, 12, 13, '2024-02-19 19:08:00', '2024-02-19 19:08:03', 35),
(36, 'astride jumps (male)', '36.gif', 'quadriceps,hamstrings,calves', '[\"Stand with your feet shoulder-width apart.\",\"Bend your knees and lower your body into a squat position.\",\"Jump explosively upwards, extending your legs and arms.\",\"While in the air, spread your legs apart and bring your arms out to the sides.\",\"Land softly with your feet shoulder-width apart, bending your knees to absorb the impact.\",\"Repeat for the desired number of repetitions.\"]', 'en', 2, 4, 6, '2024-02-19 19:08:03', '2024-02-19 19:08:06', 36),
(37, 'back and forth step', '37.gif', 'quadriceps,hamstrings,glutes,calves', '[\"Stand with your feet shoulder-width apart.\",\"Step forward with your right foot, bending your knee and lowering your body into a lunge position.\",\"Push off with your right foot and step back to the starting position.\",\"Repeat the movement with your left foot, alternating legs with each step.\",\"Continue stepping back and forth, maintaining a steady pace.\",\"Repeat for the desired duration or number of repetitions.\"]', 'en', 2, 4, 6, '2024-02-19 19:08:06', '2024-02-19 19:08:09', 37),
(38, 'back extension on exercise ball', '38.gif', 'glutes,hamstrings', '[\"Place the stability ball on the ground and lie face down on top of it, with your hips resting on the ball and your feet against a wall or other stable surface.\",\"Position your hands behind your head or crossed over your chest.\",\"Engage your core and slowly lift your upper body off the ball, extending your back until your body forms a straight line from your head to your heels.\",\"Pause for a moment at the top, then slowly lower your upper body back down to the starting position.\",\"Repeat for the desired number of repetitions.\"]', 'en', 1, 21, 16, '2024-02-19 19:08:09', '2024-02-19 19:08:12', 38),
(39, 'back lever', '39.gif', 'biceps,forearms,core', '[\"Start by hanging from a pull-up bar with an overhand grip, hands slightly wider than shoulder-width apart.\",\"Engage your core and pull your shoulder blades down and back.\",\"Bend your knees and tuck them towards your chest.\",\"Slowly lift your legs up, keeping them straight, until your body is parallel to the ground.\",\"Hold this position for a few seconds, then slowly lower your legs back down to the starting position.\",\"Repeat for the desired number of repetitions.\"]', 'en', 1, 4, 19, '2024-02-19 19:08:12', '2024-02-19 19:08:16', 39),
(40, 'back pec stretch', '40.gif', 'shoulders,chest', '[\"Stand tall with your feet shoulder-width apart.\",\"Extend your arms straight out in front of you, parallel to the ground.\",\"Cross your arms in front of your body, with your right arm over your left arm.\",\"Interlock your fingers and rotate your palms away from your body.\",\"Slowly raise your arms up and away from your body, feeling a stretch in your back and chest.\",\"Hold the stretch for 15-30 seconds, then release.\",\"Repeat on the opposite side.\"]', 'en', 1, 4, 11, '2024-02-19 19:08:17', '2024-02-19 19:08:19', 40),
(41, 'backward jump', '41.gif', 'hamstrings,glutes,calves', '[\"Stand with your feet shoulder-width apart.\",\"Bend your knees slightly and jump backwards, pushing off with both feet.\",\"Land softly on the balls of your feet, bending your knees to absorb the impact.\",\"Repeat for the desired number of repetitions.\"]', 'en', 9, 4, 14, '2024-02-19 19:08:19', '2024-02-19 19:08:22', 41),
(42, 'balance board', '42.gif', 'calves,hamstrings,glutes', '[\"Place the balance board on a flat surface.\",\"Step onto the balance board with one foot, ensuring it is centered.\",\"Slowly shift your weight onto the foot on the balance board, keeping your core engaged.\",\"Maintain your balance and stability as you hold the position for a desired amount of time.\",\"Repeat the exercise with the other foot.\"]', 'en', 9, 4, 14, '2024-02-19 19:08:22', '2024-02-19 19:08:25', 42),
(43, 'band alternating biceps curl', '43.gif', 'forearms', '[\"Stand with your feet shoulder-width apart and hold the band with an underhand grip, palms facing up.\",\"Keep your elbows close to your sides and slowly curl one arm up towards your shoulder, squeezing your biceps at the top.\",\"Lower the arm back down to the starting position and repeat with the other arm.\",\"Continue alternating arms for the desired number of repetitions.\"]', 'en', 8, 2, 4, '2024-02-19 19:08:25', '2024-02-19 19:08:28', 43),
(44, 'band alternating v-up', '44.gif', 'hip flexors', '[\"Lie flat on your back with your legs straight and your arms extended overhead, holding the band.\",\"Engage your abs and lift your legs and upper body off the ground simultaneously, reaching your hands towards your toes.\",\"As you lower your legs and upper body back down, switch the position of your legs, crossing one over the other.\",\"Repeat the movement, alternating the position of your legs with each repetition.\",\"Continue for the desired number of repetitions.\"]', 'en', 10, 2, 2, '2024-02-19 19:08:28', '2024-02-19 19:08:29', 44),
(45, 'band assisted pull-up', '45.gif', 'biceps,forearms', '[\"Attach the band to a pull-up bar or sturdy anchor point.\",\"Step onto the band and grip the bar with your palms facing away from you, hands slightly wider than shoulder-width apart.\",\"Hang with your arms fully extended, keeping your core engaged and your shoulders down and back.\",\"Pull your body up towards the bar by squeezing your shoulder blades together and driving your elbows down towards your hips.\",\"Continue pulling until your chin is above the bar, then slowly lower yourself back down to the starting position.\",\"Repeat for the desired number of repetitions.\"]', 'en', 1, 2, 11, '2024-02-19 19:08:29', '2024-02-19 19:08:31', 45),
(46, 'band assisted wheel rollerout', '46.gif', 'lower back', '[\"Kneel on the floor and hold the handles of the band with both hands, palms facing down.\",\"Place the band on the ground in front of you and position your hands shoulder-width apart.\",\"Engage your core and slowly roll the wheel forward, extending your body as far as you can while maintaining control.\",\"Pause for a moment at the furthest point, then slowly roll the wheel back towards your knees to return to the starting position.\",\"Repeat for the desired number of repetitions.\"]', 'en', 10, 2, 2, '2024-02-19 19:08:31', '2024-02-19 19:08:35', 46),
(47, 'band bench press', '47.gif', 'triceps,shoulders', '[\"Lie flat on a bench with your feet flat on the ground and your back pressed against the bench.\",\"Grasp the band handles with an overhand grip, slightly wider than shoulder-width apart.\",\"Extend your arms fully, pushing the bands away from your chest.\",\"Slowly lower the bands back down to your chest, keeping your elbows at a 90-degree angle.\",\"Repeat for the desired number of repetitions.\"]', 'en', 3, 2, 13, '2024-02-19 19:08:35', '2024-02-19 19:08:38', 47),
(48, 'band bent-over hip extension', '48.gif', 'hamstrings,lower back', '[\"Attach the band to a sturdy anchor point at ankle height.\",\"Stand facing away from the anchor point with your feet shoulder-width apart.\",\"Step back to create tension in the band, keeping your knees slightly bent.\",\"Hinge at the hips and lean forward, maintaining a neutral spine.\",\"Extend your right leg straight back, squeezing your glutes at the top.\",\"Lower your right leg back down and repeat with the left leg.\",\"Continue alternating legs for the desired number of repetitions.\"]', 'en', 9, 2, 9, '2024-02-19 19:08:38', '2024-02-19 19:08:41', 48),
(49, 'band bicycle crunch', '49.gif', 'hip flexors,obliques', '[\"Lie flat on your back with your hands behind your head and your knees bent.\",\"Lift your feet off the ground and bring your right knee towards your chest while simultaneously twisting your torso to bring your left elbow towards your right knee.\",\"Straighten your right leg while bringing your left knee towards your chest and twisting your torso to bring your right elbow towards your left knee.\",\"Continue alternating the twisting motion, as if you are pedaling a bicycle, while keeping your core engaged throughout the movement.\",\"Repeat for the desired number of repetitions.\"]', 'en', 10, 2, 2, '2024-02-19 19:08:41', '2024-02-19 19:08:44', 49),
(50, 'band close-grip pulldown', '50.gif', 'biceps,forearms', '[\"Attach the band to a high anchor point, such as a pull-up bar or sturdy beam.\",\"Stand facing the anchor point and grab the band with an underhand grip, hands shoulder-width apart.\",\"Step back to create tension in the band, keeping your feet hip-width apart.\",\"Engage your core and keep your back straight as you pull the band down towards your chest, squeezing your shoulder blades together.\",\"Pause for a moment at the bottom of the movement, then slowly release the band back to the starting position.\",\"Repeat for the desired number of repetitions.\"]', 'en', 1, 2, 11, '2024-02-19 19:08:44', '2024-02-19 19:08:48', 50),
(51, 'band close-grip push-up', '51.gif', 'chest,shoulders', '[\"Place a band around your upper arms, just above the elbows.\",\"Assume a push-up position with your hands directly under your shoulders and your body in a straight line from head to heels.\",\"Bend your elbows and lower your chest towards the ground, keeping your elbows close to your sides.\",\"Push through your palms to extend your arms and return to the starting position.\",\"Repeat for the desired number of repetitions.\"]', 'en', 8, 2, 18, '2024-02-19 19:08:48', '2024-02-19 19:08:52', 51),
(52, 'band concentration curl', '52.gif', 'forearms', '[\"Sit on a bench or chair with your legs spread apart and your feet flat on the ground.\",\"Hold one end of the band in your hand and step on the other end with your foot on the same side.\",\"Lean forward slightly and rest your elbow on the inside of your thigh, just above the knee.\",\"With your palm facing up, slowly curl your hand towards your shoulder, keeping your upper arm stationary.\",\"Pause for a moment at the top, then slowly lower your hand back down to the starting position.\",\"Repeat for the desired number of repetitions, then switch sides.\"]', 'en', 8, 2, 4, '2024-02-19 19:08:52', '2024-02-19 19:08:57', 52),
(53, 'band fixed back close grip pulldown', '53.gif', 'biceps,forearms', '[\"Attach the band to a fixed point above you, such as a pull-up bar or sturdy beam.\",\"Sit down on a bench or chair facing the band, with your feet flat on the ground and your knees bent.\",\"Grasp the band with a close grip, palms facing towards you.\",\"Keep your back straight and engage your core.\",\"Pull the band down towards your chest, squeezing your shoulder blades together.\",\"Pause for a moment at the bottom of the movement, feeling the contraction in your lats.\",\"Slowly release the band back to the starting position, maintaining control throughout the movement.\",\"Repeat for the desired number of repetitions.\"]', 'en', 1, 2, 11, '2024-02-19 19:08:57', '2024-02-19 19:09:00', 53),
(54, 'band fixed back underhand pulldown', '54.gif', 'biceps,rhomboids,rear deltoids', '[\"Attach the band to a sturdy anchor point above your head.\",\"Stand facing the anchor point with your feet shoulder-width apart.\",\"Grasp the band with an underhand grip, hands slightly wider than shoulder-width apart.\",\"Step back to create tension in the band, keeping your arms fully extended.\",\"Engage your core and squeeze your shoulder blades together.\",\"Pull the band down towards your chest, leading with your elbows.\",\"Pause for a moment at the bottom of the movement, feeling the contraction in your lats.\",\"Slowly release the tension in the band and return to the starting position.\",\"Repeat for the desired number of repetitions.\"]', 'en', 1, 2, 11, '2024-02-19 19:09:01', '2024-02-19 19:09:03', 54),
(55, 'band front lateral raise', '55.gif', 'traps,upper back', '[\"Stand with your feet shoulder-width apart and hold the band in front of your thighs with your palms facing down.\",\"Keep your arms straight and lift the band up in front of you until your arms are parallel to the ground.\",\"Pause for a moment at the top, then slowly lower the band back down to the starting position.\",\"Repeat for the desired number of repetitions.\"]', 'en', 7, 2, 7, '2024-02-19 19:09:03', '2024-02-19 19:09:06', 55),
(56, 'band front raise', '56.gif', 'triceps,upper back', '[\"Stand with your feet shoulder-width apart and hold the band in front of your thighs with your palms facing down.\",\"Keep your arms straight and slowly raise them forward until they are parallel to the ground.\",\"Pause for a moment at the top, then slowly lower your arms back down to the starting position.\",\"Repeat for the desired number of repetitions.\"]', 'en', 7, 2, 7, '2024-02-19 19:09:06', '2024-02-19 19:09:09', 56),
(57, 'band hip lift', '57.gif', 'hamstrings,quadriceps', '[\"Lie on your back with your knees bent and feet flat on the ground.\",\"Place a resistance band just above your knees.\",\"Engage your glutes and core muscles.\",\"Press your heels into the ground and lift your hips off the floor, squeezing your glutes at the top.\",\"Pause for a moment at the top, then slowly lower your hips back down to the starting position.\",\"Repeat for the desired number of repetitions.\"]', 'en', 9, 2, 9, '2024-02-19 19:09:09', '2024-02-19 19:09:12', 57),
(58, 'band horizontal pallof press', '58.gif', 'obliques,glutes', '[\"Attach the band to a sturdy anchor point at waist height.\",\"Stand perpendicular to the anchor point with your feet shoulder-width apart.\",\"Grasp the band handle with both hands and step away from the anchor point to create tension in the band.\",\"Bring your hands to your chest, keeping your elbows bent and close to your body.\",\"Engage your core and maintain a stable stance.\",\"Extend your arms straight out in front of you, pushing the band away from your body.\",\"Hold the extended position for a few seconds, focusing on maintaining tension in your core.\",\"Slowly bring your hands back to your chest, resisting the pull of the band.\",\"Repeat for the desired number of repetitions.\"]', 'en', 10, 2, 2, '2024-02-19 19:09:12', '2024-02-19 19:09:15', 58),
(59, 'band jack knife sit-up', '59.gif', 'hip flexors', '[\"Lie flat on your back with your legs straight and your arms extended overhead, holding the band.\",\"Engage your abs and lift your legs and upper body simultaneously, bringing your hands towards your feet.\",\"Pause for a moment at the top, then slowly lower your legs and upper body back down to the starting position.\",\"Repeat for the desired number of repetitions.\"]', 'en', 10, 2, 2, '2024-02-19 19:09:15', '2024-02-19 19:09:19', 59),
(60, 'band kneeling one arm pulldown', '60.gif', 'biceps,rhomboids', '[\"Attach the band to a sturdy anchor point above your head.\",\"Kneel down and hold the band with one hand, palm facing down.\",\"Extend your arm fully overhead, keeping your elbow slightly bent.\",\"Engage your lat muscles and pull the band down towards your side, bringing your elbow towards your ribcage.\",\"Pause for a moment at the bottom, then slowly release the tension and return to the starting position.\",\"Repeat for the desired number of repetitions, then switch sides.\"]', 'en', 1, 2, 11, '2024-02-19 19:09:19', '2024-02-19 19:09:22', 60),
(61, 'band kneeling twisting crunch', '61.gif', 'obliques', '[\"Attach the band to a sturdy anchor point at waist height.\",\"Kneel down facing away from the anchor point and hold the band with both hands, keeping your elbows bent and close to your sides.\",\"Engage your abs and slowly twist your torso to one side, bringing your hands towards your opposite hip.\",\"Pause for a moment, then slowly return to the starting position.\",\"Repeat on the other side.\",\"Continue alternating sides for the desired number of repetitions.\"]', 'en', 10, 2, 2, '2024-02-19 19:09:22', '2024-02-19 19:09:25', 61),
(62, 'band lying hip internal rotation', '62.gif', 'hamstrings,quadriceps', '[\"Lie on your back with your legs straight and a resistance band looped around your feet.\",\"Bend your knees and bring them towards your chest, keeping your feet together.\",\"Slowly rotate your knees outwards, away from each other, while keeping your feet together.\",\"Pause for a moment at the end of the rotation, then slowly return to the starting position.\",\"Repeat for the desired number of repetitions.\"]', 'en', 9, 2, 9, '2024-02-19 19:09:25', '2024-02-19 19:09:28', 62),
(63, 'band lying straight leg raise', '63.gif', 'hip flexors', '[\"Lie flat on your back with your legs straight and your feet together.\",\"Place the band around the arches of your feet and hold the ends of the band with your hands.\",\"Engaging your abs, lift both legs off the ground until they are perpendicular to the floor.\",\"Pause for a moment at the top, then slowly lower your legs back down to the starting position.\",\"Repeat for the desired number of repetitions.\"]', 'en', 10, 2, 2, '2024-02-19 19:09:28', '2024-02-19 19:09:31', 63),
(64, 'band one arm overhead biceps curl', '64.gif', 'forearms,shoulders', '[\"Stand with your feet shoulder-width apart and place one end of the band under your foot.\",\"Hold the other end of the band with your arm fully extended overhead, palm facing forward.\",\"Keeping your upper arm stationary, curl your forearm towards your shoulder, squeezing your biceps.\",\"Pause for a moment at the top, then slowly lower your forearm back to the starting position.\",\"Repeat for the desired number of repetitions, then switch arms.\"]', 'en', 8, 2, 4, '2024-02-19 19:09:31', '2024-02-19 19:09:34', 64),
(65, 'band one arm single leg split squat', '65.gif', 'glutes,hamstrings', '[\"Stand with your feet hip-width apart and place a resistance band around your ankles.\",\"Extend one leg forward and rest the top of your foot on a bench or step behind you.\",\"Hold onto a support with one hand for balance.\",\"Bend your standing leg and lower your body down into a squat position, keeping your knee in line with your toes.\",\"Push through your heel to return to the starting position.\",\"Repeat for the desired number of repetitions, then switch legs.\"]', 'en', 9, 2, 14, '2024-02-19 19:09:34', '2024-02-19 19:09:37', 65),
(66, 'band one arm standing low row', '66.gif', 'biceps,shoulders', '[\"Attach the band to a stable anchor point at waist height.\",\"Stand facing the anchor point with your feet shoulder-width apart.\",\"Hold the band with one hand, palm facing inward, and step back to create tension in the band.\",\"Bend your knees slightly and hinge forward at the hips, keeping your back straight.\",\"Pull the band towards your waist, squeezing your shoulder blades together.\",\"Pause for a moment at the top of the movement, then slowly release the band back to the starting position.\",\"Repeat for the desired number of repetitions, then switch sides.\"]', 'en', 1, 2, 19, '2024-02-19 19:09:37', '2024-02-19 19:09:41', 66),
(67, 'band one arm twisting chest press', '67.gif', 'shoulders,triceps', '[\"Attach the band to a sturdy anchor point at chest height.\",\"Stand with your side facing the anchor point and grab the band with one hand.\",\"Step away from the anchor point to create tension in the band.\",\"Position your feet shoulder-width apart and slightly bend your knees.\",\"Bring your hand holding the band across your body, towards the opposite shoulder.\",\"While maintaining tension in the band, push your hand forward and away from your body, extending your arm.\",\"Slowly return to the starting position and repeat for the desired number of repetitions.\",\"Switch sides and repeat the exercise with the other hand.\"]', 'en', 3, 2, 13, '2024-02-19 19:09:41', '2024-02-19 19:09:45', 67),
(68, 'band one arm twisting seated row', '68.gif', 'biceps,shoulders', '[\"Sit on a bench or chair with your feet flat on the ground and your back straight.\",\"Hold the band with one hand and extend your arm fully in front of you.\",\"Keeping your back straight, pull the band towards your body by bending your elbow and squeezing your shoulder blades together.\",\"At the same time, twist your torso towards the side of the pulling arm.\",\"Pause for a moment at the top, then slowly release the tension in the band and return to the starting position.\",\"Repeat for the desired number of repetitions, then switch to the other arm.\"]', 'en', 1, 2, 19, '2024-02-19 19:09:45', '2024-02-19 19:09:47', 68),
(69, 'band pull through', '69.gif', 'hamstrings,lower back', '[\"Attach a resistance band to a sturdy anchor point at ground level.\",\"Stand facing away from the anchor point with your feet shoulder-width apart.\",\"Step forward to create tension in the band, keeping your knees slightly bent.\",\"Hinge at the hips and push your glutes back, maintaining a slight bend in your knees.\",\"Lower your torso until it is parallel to the ground, feeling a stretch in your hamstrings.\",\"Drive your hips forward and squeeze your glutes to return to the starting position.\",\"Repeat for the desired number of repetitions.\"]', 'en', 9, 2, 9, '2024-02-19 19:09:47', '2024-02-19 19:09:50', 69),
(70, 'band push sit-up', '70.gif', 'shoulders,chest', '[\"Attach the band securely to a stable anchor point.\",\"Lie flat on your back with your knees bent and feet flat on the ground.\",\"Hold the band with both hands and extend your arms straight up towards the ceiling.\",\"Engaging your abs, slowly lift your upper body off the ground, curling forward until your torso is at a 45-degree angle.\",\"Pause for a moment at the top, then slowly lower your upper body back down to the starting position.\",\"Repeat for the desired number of repetitions.\"]', 'en', 10, 2, 2, '2024-02-19 19:09:50', '2024-02-19 19:09:53', 70),
(71, 'band reverse fly', '71.gif', 'upper back,trapezius', '[\"Attach the band to a stationary object at chest height.\",\"Stand with your feet shoulder-width apart and hold the band with both hands in front of you.\",\"Keep your arms straight and lift them out to the sides until they are parallel to the ground.\",\"Squeeze your shoulder blades together at the top of the movement.\",\"Slowly lower your arms back to the starting position.\",\"Repeat for the desired number of repetitions.\"]', 'en', 7, 2, 7, '2024-02-19 19:09:53', '2024-02-19 19:09:57', 71),
(72, 'band reverse wrist curl', '72.gif', 'forearms', '[\"Sit on a bench or chair with your feet flat on the ground.\",\"Hold the band with an overhand grip, palms facing down, and wrap it around your fingers.\",\"Rest your forearms on your thighs, with your wrists hanging off the edge.\",\"Slowly curl your wrists upward, squeezing your forearms.\",\"Pause for a moment at the top, then slowly lower your wrists back down to the starting position.\",\"Repeat for the desired number of repetitions.\"]', 'en', 4, 2, 8, '2024-02-19 19:09:57', '2024-02-19 19:10:01', 72),
(73, 'band seated hip internal rotation', '73.gif', 'hamstrings,quadriceps', '[\"Sit on a chair or bench with your back straight and feet flat on the ground.\",\"Place a resistance band around your thighs, just above your knees.\",\"Keep your knees bent at a 90-degree angle and your feet shoulder-width apart.\",\"Engage your glutes and slowly rotate your knees outward, pushing against the resistance of the band.\",\"Pause for a moment at the end of the movement, then slowly return to the starting position.\",\"Repeat for the desired number of repetitions.\"]', 'en', 9, 2, 9, '2024-02-19 19:10:01', '2024-02-19 19:10:05', 73),
(74, 'band seated twist', '74.gif', 'obliques', '[\"Sit on the ground with your legs extended in front of you and your back straight.\",\"Wrap the band around your waist and hold the ends with both hands.\",\"Engage your abs and slowly twist your torso to one side, keeping your back straight and your feet on the ground.\",\"Pause for a moment at the end of the twist, then slowly return to the starting position.\",\"Repeat the twist to the other side.\",\"Continue alternating sides for the desired number of repetitions.\"]', 'en', 10, 2, 2, '2024-02-19 19:10:05', '2024-02-19 19:10:07', 74),
(75, 'band shoulder press', '75.gif', 'triceps,upper back', '[\"Stand with your feet shoulder-width apart and place the band under your feet.\",\"Hold the band with your palms facing forward and raise your hands to shoulder height, elbows bent.\",\"Press the band overhead, fully extending your arms.\",\"Pause for a moment at the top, then slowly lower the band back to shoulder height.\",\"Repeat for the desired number of repetitions.\"]', 'en', 7, 2, 7, '2024-02-19 19:10:07', '2024-02-19 19:10:11', 75),
(76, 'band shrug', '76.gif', 'shoulders', '[\"Stand with your feet shoulder-width apart and place the band under your feet, holding the ends with your hands.\",\"Keep your arms straight and relaxed, and let the band hang in front of your thighs.\",\"Engage your traps by shrugging your shoulders upward, lifting the band as high as possible.\",\"Hold the contraction for a moment, then slowly lower your shoulders back down to the starting position.\",\"Repeat for the desired number of repetitions.\"]', 'en', 1, 2, 17, '2024-02-19 19:10:11', '2024-02-19 19:10:13', 76),
(77, 'band side triceps extension', '77.gif', 'shoulders', '[\"Stand with your feet shoulder-width apart and hold the band with both hands, palms facing down.\",\"Extend your arms straight out to the sides, keeping them parallel to the ground.\",\"Slowly bend your elbows and bring your hands towards your shoulders, keeping your upper arms still.\",\"Pause for a moment, then slowly extend your arms back out to the starting position.\",\"Repeat for the desired number of repetitions.\"]', 'en', 8, 2, 18, '2024-02-19 19:10:13', '2024-02-19 19:10:16', 77),
(78, 'band single leg calf raise', '78.gif', 'ankles,feet', '[\"Stand with your feet hip-width apart and place the band around the ball of your left foot.\",\"Hold onto a stable object for balance if needed.\",\"Slowly raise your left heel off the ground, lifting your body weight onto the ball of your foot.\",\"Pause for a moment at the top, then slowly lower your left heel back down to the starting position.\",\"Repeat for the desired number of repetitions, then switch to the right leg.\"]', 'en', 5, 2, 5, '2024-02-19 19:10:16', '2024-02-19 19:10:21', 78),
(79, 'band single leg reverse calf raise', '79.gif', 'hamstrings,glutes', '[\"Stand with your feet hip-width apart and place the band around the ball of your foot.\",\"Hold onto a stable object for balance.\",\"Slowly raise your heel off the ground, lifting your body weight onto the ball of your foot.\",\"Pause for a moment at the top, then slowly lower your heel back down to the starting position.\",\"Repeat for the desired number of repetitions, then switch to the other leg.\"]', 'en', 5, 2, 5, '2024-02-19 19:10:21', '2024-02-19 19:10:25', 79),
(80, 'band single leg split squat', '80.gif', 'glutes,hamstrings', '[\"Stand with your feet hip-width apart and place a resistance band around your ankles.\",\"Take a big step forward with your right foot and a smaller step back with your left foot.\",\"Bend your knees and lower your body until your right thigh is parallel to the ground, keeping your left knee slightly above the ground.\",\"Push through your right heel to return to the starting position.\",\"Repeat on the other side.\"]', 'en', 9, 2, 14, '2024-02-19 19:10:25', '2024-02-19 19:10:27', 80),
(81, 'band squat', '81.gif', 'quadriceps,hamstrings,calves', '[\"Stand with your feet shoulder-width apart, with the band placed just above your knees.\",\"Keeping your chest up and core engaged, push your hips back and bend your knees to lower into a squat position.\",\"Make sure your knees are tracking over your toes and your weight is in your heels.\",\"Pause for a moment at the bottom, then push through your heels to return to the starting position.\",\"Repeat for the desired number of repetitions.\"]', 'en', 9, 2, 9, '2024-02-19 19:10:27', '2024-02-19 19:10:29', 81),
(82, 'band squat row', '82.gif', 'hamstrings,quadriceps,back', '[\"Attach the band to a sturdy anchor point at waist height.\",\"Stand facing the anchor point with your feet shoulder-width apart.\",\"Hold the band handles with your palms facing each other and your arms extended in front of you.\",\"Bend your knees and lower into a squat position, keeping your back straight and chest lifted.\",\"From the squat position, pull the band handles towards your body, squeezing your shoulder blades together.\",\"Pause for a moment at the top, then slowly release the tension and return to the starting position.\",\"Repeat for the desired number of repetitions.\"]', 'en', 9, 2, 9, '2024-02-19 19:10:29', '2024-02-19 19:10:32', 82),
(83, 'band standing crunch', '83.gif', 'obliques', '[\"Attach the band to a sturdy anchor point at waist height.\",\"Stand facing away from the anchor point with your feet shoulder-width apart.\",\"Hold the band with both hands and bring it up to your chest, keeping your elbows bent and close to your body.\",\"Engage your abs and slowly crunch forward, bringing your chest towards your knees.\",\"Pause for a moment at the top of the crunch, then slowly return to the starting position.\",\"Repeat for the desired number of repetitions.\"]', 'en', 10, 2, 2, '2024-02-19 19:10:32', '2024-02-19 19:10:35', 83),
(84, 'band standing rear delt row', '84.gif', 'trapezius,rhomboids,biceps', '[\"Stand with your feet shoulder-width apart and place the band under your feet.\",\"Hold the band handles with your palms facing each other and your arms extended in front of you.\",\"Bend your knees slightly and hinge forward at the hips, keeping your back straight.\",\"Pull the band towards your chest, squeezing your shoulder blades together.\",\"Pause for a moment at the top, then slowly release the tension and return to the starting position.\",\"Repeat for the desired number of repetitions.\"]', 'en', 7, 2, 7, '2024-02-19 19:10:35', '2024-02-19 19:10:36', 84);
INSERT INTO `exercises` (`id`, `name`, `gif`, `secondary_muscles`, `instructions`, `lang`, `body_part_id`, `equipment_id`, `target_id`, `created_at`, `updated_at`, `exercise_id`) VALUES
(85, 'band standing twisting crunch', '85.gif', 'obliques', '[\"Stand with your feet shoulder-width apart and place the band around your upper back, crossing it in front of your chest.\",\"Hold the ends of the band with your hands, keeping your elbows bent and close to your sides.\",\"Engage your abs and twist your torso to one side, bringing your elbow towards the opposite knee.\",\"Pause for a moment, then return to the starting position.\",\"Repeat on the other side.\",\"Continue alternating sides for the desired number of repetitions.\"]', 'en', 10, 2, 2, '2024-02-19 19:10:36', '2024-02-19 19:10:39', 85),
(86, 'band step-up', '86.gif', 'quadriceps,hamstrings,calves', '[\"Place a band around your thighs, just above your knees.\",\"Stand facing a step or platform with your feet hip-width apart.\",\"Step up onto the platform with your right foot, pushing through your heel.\",\"Extend your left leg behind you, keeping it straight.\",\"Lower your left foot back down to the ground.\",\"Repeat with your left foot stepping up onto the platform.\",\"Continue alternating legs for the desired number of repetitions.\"]', 'en', 9, 2, 9, '2024-02-19 19:10:39', '2024-02-19 19:10:43', 86),
(87, 'band stiff leg deadlift', '87.gif', 'hamstrings,lower back', '[\"Stand with your feet shoulder-width apart and place the band around your ankles.\",\"Hold the band with both hands in front of your thighs, palms facing your body.\",\"Keeping your back straight and your core engaged, hinge at the hips and slowly lower your upper body towards the ground.\",\"As you lower, push your hips back and allow your knees to bend slightly.\",\"Lower the band towards the ground, feeling a stretch in your hamstrings.\",\"Pause for a moment at the bottom, then engage your glutes and hamstrings to lift your upper body back up to the starting position.\",\"Repeat for the desired number of repetitions.\"]', 'en', 9, 2, 9, '2024-02-19 19:10:43', '2024-02-19 19:10:47', 87),
(88, 'band straight back stiff leg deadlift', '88.gif', 'hamstrings,lower back', '[\"Stand with your feet shoulder-width apart and place the band around your upper legs.\",\"Hold the band with both hands in front of your thighs, palms facing your body.\",\"Keeping your back straight and your knees slightly bent, hinge at the hips and lower the band towards the ground.\",\"Feel the stretch in your hamstrings as you lower the band.\",\"Engage your glutes and hamstrings to lift your body back up to the starting position.\",\"Repeat for the desired number of repetitions.\"]', 'en', 9, 2, 9, '2024-02-19 19:10:47', '2024-02-19 19:10:49', 88),
(89, 'band straight leg deadlift', '89.gif', 'hamstrings,glutes', '[\"Stand with your feet shoulder-width apart and place the band around your feet.\",\"Hold the band with both hands, palms facing your body, and keep your arms straight.\",\"Engage your core and maintain a slight bend in your knees.\",\"Slowly hinge forward at your hips, keeping your back straight and chest lifted.\",\"Lower the band towards the ground while keeping your legs straight.\",\"Pause for a moment at the bottom, then squeeze your glutes and hamstrings to return to the starting position.\",\"Repeat for the desired number of repetitions.\"]', 'en', 1, 2, 16, '2024-02-19 19:10:49', '2024-02-19 19:10:53', 89),
(90, 'band twisting overhead press', '90.gif', 'triceps,upper back', '[\"Stand with your feet shoulder-width apart and place the band under your feet.\",\"Hold the band handles at shoulder height with your palms facing forward.\",\"Engage your core and press the band overhead, fully extending your arms.\",\"As you press, twist your torso to one side, keeping your hips stable.\",\"Pause for a moment at the top, then return to the starting position.\",\"Repeat the press and twist on the opposite side.\",\"Continue alternating sides for the desired number of repetitions.\"]', 'en', 7, 2, 7, '2024-02-19 19:10:53', '2024-02-19 19:10:55', 90),
(91, 'band two legs calf raise - (band under both legs) v. 2', '91.gif', 'ankles,feet', '[\"Stand with your feet shoulder-width apart and place a resistance band under both feet.\",\"Hold the ends of the band with your hands for stability.\",\"Raise your heels off the ground as high as possible, using your calves.\",\"Pause for a moment at the top, then slowly lower your heels back down to the starting position.\",\"Repeat for the desired number of repetitions.\"]', 'en', 5, 2, 5, '2024-02-19 19:10:55', '2024-02-19 19:10:58', 91),
(92, 'band underhand pulldown', '92.gif', 'biceps,forearms', '[\"Attach the band to a high anchor point, such as a pull-up bar or sturdy beam.\",\"Stand facing the anchor point with your feet shoulder-width apart.\",\"Grasp the band with an underhand grip, hands slightly wider than shoulder-width apart.\",\"Extend your arms fully overhead, keeping your elbows slightly bent.\",\"Engage your lats and pull the band down towards your chest, squeezing your shoulder blades together.\",\"Pause for a moment at the bottom of the movement, then slowly release the tension and return to the starting position.\",\"Repeat for the desired number of repetitions.\"]', 'en', 1, 2, 11, '2024-02-19 19:10:58', '2024-02-19 19:11:01', 92),
(93, 'band v-up', '93.gif', 'hip flexors', '[\"Lie flat on your back with your legs straight and your arms extended overhead, holding the band.\",\"Engaging your abs, lift your legs and upper body off the ground simultaneously, reaching your hands towards your toes.\",\"Pause for a moment at the top, then slowly lower your legs and upper body back down to the starting position.\",\"Repeat for the desired number of repetitions.\"]', 'en', 10, 2, 2, '2024-02-19 19:11:01', '2024-02-19 19:11:04', 93),
(94, 'band vertical pallof press', '94.gif', 'obliques,glutes', '[\"Stand with your feet shoulder-width apart and wrap the band around a sturdy object at chest height.\",\"Hold the band with both hands and step away from the anchor point, creating tension in the band.\",\"Position yourself perpendicular to the anchor point, with your side facing the band.\",\"Extend your arms straight out in front of you, keeping your hands at chest height.\",\"Engage your core and press the band away from your chest, fully extending your arms.\",\"Hold the position for a few seconds, then slowly bring the band back towards your chest.\",\"Repeat for the desired number of repetitions, then switch sides.\"]', 'en', 10, 2, 2, '2024-02-19 19:11:04', '2024-02-19 19:11:08', 94),
(95, 'band wrist curl', '95.gif', 'biceps,triceps', '[\"Sit on a bench or chair with your feet flat on the ground.\",\"Hold the band with both hands, palms facing up, and rest your forearms on your thighs.\",\"Slowly curl your wrists upward, squeezing your forearms.\",\"Pause for a moment at the top, then slowly lower your wrists back down to the starting position.\",\"Repeat for the desired number of repetitions.\"]', 'en', 4, 2, 8, '2024-02-19 19:11:08', '2024-02-19 19:11:11', 95),
(96, 'band y-raise', '96.gif', 'traps,rhomboids', '[\"Stand with your feet shoulder-width apart and hold the band in front of your thighs with your palms facing inwards.\",\"Keep your arms straight and lift them up and out to the sides, forming a \'Y\' shape with your body.\",\"Squeeze your shoulder blades together at the top of the movement.\",\"Slowly lower your arms back down to the starting position.\",\"Repeat for the desired number of repetitions.\"]', 'en', 7, 2, 7, '2024-02-19 19:11:11', '2024-02-19 19:11:13', 96),
(97, 'barbell alternate biceps curl', '97.gif', 'forearms', '[\"Stand up straight with your feet shoulder-width apart and hold a barbell in each hand, palms facing forward.\",\"Keep your upper arms stationary and exhale as you curl the weights while contracting your biceps.\",\"Continue to raise the barbells until your biceps are fully contracted and the barbells are at shoulder level.\",\"Hold the contracted position for a brief pause as you squeeze your biceps.\",\"Inhale as you slowly begin to lower the barbells back to the starting position.\",\"Repeat for the desired number of repetitions, alternating arms.\"]', 'en', 8, 3, 4, '2024-02-19 19:11:13', '2024-02-19 19:11:16', 97),
(98, 'barbell bench front squat', '98.gif', 'hamstrings,glutes,calves', '[\"Start by standing with your feet shoulder-width apart and the barbell resting on your upper chest, just below your collarbone.\",\"Hold the barbell with an overhand grip, keeping your elbows up and your upper arms parallel to the ground.\",\"Lower your body down into a squat position by bending at the knees and hips, keeping your back straight and your chest up.\",\"Pause for a moment at the bottom of the squat, then push through your heels to return to the starting position.\",\"Repeat for the desired number of repetitions.\"]', 'en', 9, 3, 14, '2024-02-19 19:11:16', '2024-02-19 19:11:18', 98),
(99, 'barbell bench press', '99.gif', 'triceps,shoulders', '[\"Lie flat on a bench with your feet flat on the ground and your back pressed against the bench.\",\"Grasp the barbell with an overhand grip slightly wider than shoulder-width apart.\",\"Lift the barbell off the rack and hold it directly above your chest with your arms fully extended.\",\"Lower the barbell slowly towards your chest, keeping your elbows tucked in.\",\"Pause for a moment when the barbell touches your chest.\",\"Push the barbell back up to the starting position by extending your arms.\",\"Repeat for the desired number of repetitions.\"]', 'en', 3, 3, 13, '2024-02-19 19:11:18', '2024-02-19 19:11:22', 99),
(100, 'barbell bench squat', '100.gif', 'glutes,hamstrings,calves', '[\"Set up a barbell on a squat rack at chest height.\",\"Stand facing away from the rack, with your feet shoulder-width apart.\",\"Bend your knees and lower your body down into a squat position, keeping your back straight and chest up.\",\"Grasp the barbell with an overhand grip, slightly wider than shoulder-width apart.\",\"Lift the barbell off the rack and step back, ensuring your feet are still shoulder-width apart.\",\"Lower your body down into a squat, keeping your knees in line with your toes.\",\"Pause for a moment at the bottom, then push through your heels to return to the starting position.\",\"Repeat for the desired number of repetitions.\"]', 'en', 9, 3, 14, '2024-02-19 19:11:22', '2024-02-19 19:11:25', 100),
(101, 'barbell bent arm pullover', '101.gif', 'triceps,chest', '[\"Lie flat on a bench with your head at one end and your feet on the floor.\",\"Hold a barbell with a shoulder-width grip and extend your arms straight above your chest.\",\"Lower the barbell behind your head while keeping your arms slightly bent.\",\"Pause for a moment, then raise the barbell back to the starting position.\",\"Repeat for the desired number of repetitions.\"]', 'en', 1, 3, 11, '2024-02-19 19:11:25', '2024-02-19 19:11:29', 101),
(102, 'barbell bent over row', '102.gif', 'biceps,forearms', '[\"Stand with your feet shoulder-width apart and knees slightly bent.\",\"Bend forward at the hips while keeping your back straight and chest up.\",\"Grasp the barbell with an overhand grip, hands slightly wider than shoulder-width apart.\",\"Pull the barbell towards your lower chest by retracting your shoulder blades and squeezing your back muscles.\",\"Pause for a moment at the top, then slowly lower the barbell back to the starting position.\",\"Repeat for the desired number of repetitions.\"]', 'en', 1, 3, 19, '2024-02-19 19:11:29', '2024-02-19 19:11:33', 102),
(103, 'barbell biceps curl (with arm blaster)', '103.gif', 'forearms', '[\"Stand up straight with your feet shoulder-width apart and hold a barbell with an underhand grip, palms facing up.\",\"Place your upper arms against the arm blaster, keeping your elbows close to your torso.\",\"Keeping your upper arms stationary, exhale and curl the weights while contracting your biceps.\",\"Continue to raise the barbell until your biceps are fully contracted and the bar is at shoulder level.\",\"Hold the contracted position for a brief pause as you squeeze your biceps.\",\"Inhale and slowly begin to lower the barbell back to the starting position.\",\"Repeat for the desired number of repetitions.\"]', 'en', 8, 3, 4, '2024-02-19 19:11:33', '2024-02-19 19:11:37', 103),
(104, 'barbell clean and press', '104.gif', 'hamstrings,glutes,shoulders,triceps', '[\"Stand with your feet shoulder-width apart and the barbell on the floor in front of you.\",\"Bend your knees and hinge at the hips to lower down and grip the barbell with an overhand grip, hands slightly wider than shoulder-width apart.\",\"Drive through your heels and extend your hips and knees to lift the barbell off the floor, keeping it close to your body.\",\"As the barbell reaches your thighs, explosively extend your hips, shrug your shoulders, and pull the barbell up towards your chest.\",\"As the barbell reaches chest height, quickly drop under it and catch it at shoulder level, with your elbows pointing forward and your palms facing up.\",\"From the catch position, press the barbell overhead by extending your arms and pushing the barbell straight up.\",\"Lower the barbell back down to the starting position and repeat for the desired number of repetitions.\"]', 'en', 9, 3, 14, '2024-02-19 19:11:37', '2024-02-19 19:11:40', 104),
(105, 'barbell clean-grip front squat', '105.gif', 'quadriceps,hamstrings,calves,core', '[\"Start by standing with your feet shoulder-width apart and the barbell resting on your upper chest, with your elbows pointing forward.\",\"Lower your body by bending your knees and pushing your hips back, as if you are sitting back into a chair.\",\"Keep your chest up and your back straight as you lower down, making sure your knees do not go past your toes.\",\"Continue lowering until your thighs are parallel to the ground, or as low as you can comfortably go.\",\"Pause for a moment at the bottom, then push through your heels to stand back up, extending your hips and knees.\",\"Repeat for the desired number of repetitions.\"]', 'en', 9, 3, 9, '2024-02-19 19:11:40', '2024-02-19 19:11:45', 105),
(106, 'barbell close-grip bench press', '106.gif', 'chest,shoulders', '[\"Lie flat on a bench with your feet flat on the ground and your back pressed against the bench.\",\"Grasp the barbell with a close grip, slightly narrower than shoulder-width apart.\",\"Unrack the barbell and lower it slowly towards your chest, keeping your elbows close to your body.\",\"Pause for a moment when the barbell touches your chest.\",\"Push the barbell back up to the starting position, fully extending your arms.\",\"Repeat for the desired number of repetitions.\"]', 'en', 8, 3, 18, '2024-02-19 19:11:45', '2024-02-19 19:11:49', 106),
(107, 'barbell curl', '107.gif', 'forearms', '[\"Stand up straight with your feet shoulder-width apart and hold a barbell with an underhand grip, palms facing forward.\",\"Keep your elbows close to your torso and exhale as you curl the weights while contracting your biceps.\",\"Continue to raise the bar until your biceps are fully contracted and the bar is at shoulder level.\",\"Hold the contracted position for a brief pause as you squeeze your biceps.\",\"Inhale as you slowly begin to lower the bar back to the starting position.\",\"Repeat for the desired number of repetitions.\"]', 'en', 8, 3, 4, '2024-02-19 19:11:49', '2024-02-19 19:11:53', 107),
(108, 'barbell deadlift', '108.gif', 'hamstrings,lower back', '[\"Stand with your feet shoulder-width apart and the barbell on the ground in front of you.\",\"Bend your knees and hinge at the hips to lower your torso and grip the barbell with an overhand grip, hands slightly wider than shoulder-width apart.\",\"Keep your back straight and chest lifted as you drive through your heels to lift the barbell off the ground, extending your hips and knees.\",\"As you stand up straight, squeeze your glutes and keep your core engaged.\",\"Lower the barbell back down to the ground by bending at the hips and knees, keeping your back straight.\",\"Repeat for the desired number of repetitions.\"]', 'en', 9, 3, 9, '2024-02-19 19:11:53', '2024-02-19 19:11:56', 108),
(109, 'barbell decline bench press', '109.gif', 'triceps,shoulders', '[\"Lie on a decline bench with your feet secured and your head lower than your hips.\",\"Grasp the barbell with an overhand grip slightly wider than shoulder-width apart.\",\"Unrack the barbell and lower it slowly towards your chest, keeping your elbows tucked in.\",\"Pause for a moment at the bottom, then push the barbell back up to the starting position.\",\"Repeat for the desired number of repetitions.\"]', 'en', 3, 3, 13, '2024-02-19 19:11:56', '2024-02-19 19:11:58', 109),
(110, 'barbell decline bent arm pullover', '110.gif', 'triceps,chest', '[\"Lie down on a decline bench with your head lower than your hips and your feet secured.\",\"Hold a barbell with a pronated grip (palms facing away from you) and extend your arms straight above your chest.\",\"Lower the barbell behind your head in a controlled manner, keeping your arms slightly bent.\",\"Pause for a moment, then raise the barbell back to the starting position by contracting your lats.\",\"Repeat for the desired number of repetitions.\"]', 'en', 1, 3, 11, '2024-02-19 19:11:58', '2024-02-19 19:12:01', 110),
(111, 'barbell decline close grip to skull press', '111.gif', 'chest,shoulders', '[\"Lie on a decline bench with your head lower than your feet and hold a barbell with a close grip.\",\"Lower the barbell towards your forehead by bending your elbows, keeping your upper arms stationary.\",\"Pause for a moment, then extend your arms to press the barbell back up to the starting position.\",\"Repeat for the desired number of repetitions.\"]', 'en', 8, 3, 18, '2024-02-19 19:12:01', '2024-02-19 19:12:05', 111),
(112, 'barbell decline pullover', '112.gif', 'triceps,shoulders', '[\"Lie down on a decline bench with your head lower than your hips and your feet secured.\",\"Hold the barbell with a pronated grip (palms facing away from you) and your hands slightly wider than shoulder-width apart.\",\"Extend your arms above your chest, keeping a slight bend in your elbows.\",\"Lower the barbell in an arc motion behind your head, feeling a stretch in your chest and shoulders.\",\"Pause for a moment, then return the barbell to the starting position by reversing the motion.\",\"Repeat for the desired number of repetitions.\"]', 'en', 3, 3, 13, '2024-02-19 19:12:05', '2024-02-19 19:12:09', 112),
(113, 'barbell decline wide-grip press', '113.gif', 'triceps,shoulders', '[\"Lie on a decline bench with your feet secured and your head lower than your hips.\",\"Grasp the barbell with a wide grip, slightly wider than shoulder-width apart.\",\"Lower the barbell to your chest, keeping your elbows out to the sides.\",\"Push the barbell back up to the starting position, fully extending your arms.\",\"Repeat for the desired number of repetitions.\"]', 'en', 3, 3, 13, '2024-02-19 19:12:09', '2024-02-19 19:12:11', 113),
(114, 'barbell decline wide-grip pullover', '114.gif', 'triceps,chest', '[\"Lie on a decline bench with your head lower than your hips and your feet secured.\",\"Hold a barbell with a wide grip and extend your arms straight above your chest.\",\"Lower the barbell behind your head in a controlled manner, keeping your arms straight.\",\"Pause for a moment, then raise the barbell back to the starting position.\",\"Repeat for the desired number of repetitions.\"]', 'en', 1, 3, 11, '2024-02-19 19:12:11', '2024-02-19 19:12:14', 114),
(115, 'barbell drag curl', '115.gif', 'forearms', '[\"Stand with your feet shoulder-width apart and hold a barbell with an underhand grip, palms facing up.\",\"Let the barbell hang at arm\'s length in front of your thighs.\",\"Keeping your upper arms stationary, curl the barbell up towards your chest by contracting your biceps.\",\"Pause for a moment at the top, then slowly lower the barbell back down to the starting position.\",\"Repeat for the desired number of repetitions.\"]', 'en', 8, 3, 4, '2024-02-19 19:12:14', '2024-02-19 19:12:18', 115),
(116, 'barbell floor calf raise', '116.gif', 'hamstrings', '[\"Place a barbell on the floor in front of you.\",\"Stand with the balls of your feet on the edge of the barbell, with your heels hanging off.\",\"Hold onto a stable object for balance if needed.\",\"Raise your heels as high as possible, using your calves to lift your body.\",\"Pause for a moment at the top, then slowly lower your heels back down to the starting position.\",\"Repeat for the desired number of repetitions.\"]', 'en', 5, 3, 5, '2024-02-19 19:12:18', '2024-02-19 19:12:21', 116),
(117, 'barbell front chest squat', '117.gif', 'quadriceps,hamstrings,calves,core', '[\"Start by standing with your feet shoulder-width apart, toes slightly turned out.\",\"Hold the barbell in front of your chest with your hands shoulder-width apart, elbows pointing forward.\",\"Engage your core and keep your chest up as you lower your body down into a squat position, pushing your hips back and bending your knees.\",\"Lower until your thighs are parallel to the ground, or as low as you can comfortably go.\",\"Pause for a moment at the bottom, then push through your heels to return to the starting position.\",\"Repeat for the desired number of repetitions.\"]', 'en', 9, 3, 9, '2024-02-19 19:12:21', '2024-02-19 19:12:24', 117),
(118, 'barbell front raise', '118.gif', 'biceps,triceps', '[\"Stand with your feet shoulder-width apart and hold a barbell in front of your thighs with an overhand grip.\",\"Keep your arms straight and lift the barbell forward and upward until it reaches shoulder level.\",\"Pause for a moment at the top, then slowly lower the barbell back down to the starting position.\",\"Repeat for the desired number of repetitions.\"]', 'en', 7, 3, 7, '2024-02-19 19:12:24', '2024-02-19 19:12:27', 118),
(119, 'barbell front raise and pullover', '119.gif', 'deltoids,triceps', '[\"Stand with your feet shoulder-width apart and hold a barbell with an overhand grip, palms facing down.\",\"Keep your arms straight and raise the barbell in front of you until it reaches shoulder height.\",\"Pause for a moment at the top, then slowly lower the barbell back down to the starting position.\",\"Next, lower the barbell behind your head, keeping your arms straight.\",\"Pause for a moment at the bottom, then raise the barbell back up to the starting position.\",\"Repeat for the desired number of repetitions.\"]', 'en', 3, 3, 13, '2024-02-19 19:12:27', '2024-02-19 19:12:32', 119),
(120, 'barbell front squat', '120.gif', 'quadriceps,hamstrings,calves,core', '[\"Start by standing with your feet shoulder-width apart, toes slightly turned out.\",\"Hold the barbell in front of your shoulders, resting it on your collarbone and shoulders.\",\"Engage your core and keep your chest up as you lower your body down into a squat position, pushing your hips back and bending your knees.\",\"Lower until your thighs are parallel to the ground, or as low as you can comfortably go.\",\"Pause for a moment at the bottom, then push through your heels to return to the starting position.\",\"Repeat for the desired number of repetitions.\"]', 'en', 9, 3, 9, '2024-02-19 19:12:32', '2024-02-19 19:12:34', 120),
(121, 'barbell full squat', '121.gif', 'quadriceps,hamstrings,calves,core', '[\"Stand with your feet shoulder-width apart, toes slightly turned out.\",\"Hold the barbell across your upper back, resting it on your traps or rear delts.\",\"Engage your core and keep your chest up as you begin to lower your body down.\",\"Bend at the knees and hips, pushing your hips back and down as if sitting into a chair.\",\"Lower yourself until your thighs are parallel to the ground or slightly below.\",\"Keep your knees in line with your toes and your weight in your heels.\",\"Drive through your heels to stand back up, extending your hips and knees.\",\"Repeat for the desired number of repetitions.\"]', 'en', 9, 3, 9, '2024-02-19 19:12:34', '2024-02-19 19:12:36', 121),
(122, 'barbell full squat (back pov)', '122.gif', 'quadriceps,hamstrings,calves,core', '[\"Stand with your feet shoulder-width apart, toes slightly turned out.\",\"Hold the barbell across your upper back, resting it on your traps or rear delts.\",\"Engage your core and keep your chest up as you begin to lower your body down.\",\"Bend at the knees and hips, pushing your hips back and down as if sitting into a chair.\",\"Lower your body until your thighs are parallel to the ground or slightly below.\",\"Keep your knees in line with your toes and your weight in your heels.\",\"Drive through your heels to stand back up, extending your hips and knees.\",\"Repeat for the desired number of repetitions.\"]', 'en', 9, 3, 9, '2024-02-19 19:12:36', '2024-02-19 19:12:38', 122),
(123, 'barbell full squat (side pov)', '123.gif', 'quadriceps,hamstrings,calves,core', '[\"Stand with your feet shoulder-width apart, toes slightly turned out.\",\"Hold the barbell across your upper back, resting it on your traps or rear delts.\",\"Engage your core and keep your chest up as you begin to lower your body down.\",\"Bend at the knees and hips, pushing your hips back and down as if sitting into a chair.\",\"Lower your body until your thighs are parallel to the ground or slightly below.\",\"Keep your knees in line with your toes and your weight in your heels.\",\"Drive through your heels to stand back up, extending your hips and knees.\",\"Repeat for the desired number of repetitions.\"]', 'en', 9, 3, 9, '2024-02-19 19:12:38', '2024-02-19 19:12:40', 123),
(124, 'barbell full zercher squat', '124.gif', 'quadriceps,hamstrings,calves,core', '[\"Stand with your feet shoulder-width apart and toes slightly turned out.\",\"Hold the barbell in the crooks of your elbows, with your hands gripping the barbell for stability.\",\"Engage your core and keep your chest lifted as you lower your hips back and down into a squat position.\",\"Keep your knees in line with your toes and your weight in your heels.\",\"Lower until your thighs are parallel to the ground, or as low as you can comfortably go.\",\"Drive through your heels to stand back up, squeezing your glutes at the top of the movement.\",\"Repeat for the desired number of repetitions.\"]', 'en', 9, 3, 9, '2024-02-19 19:12:40', '2024-02-19 19:12:43', 124),
(125, 'barbell glute bridge', '125.gif', 'hamstrings,lower back', '[\"Start by lying flat on your back on the ground with your knees bent and feet flat on the floor.\",\"Place a barbell across your hips, holding it securely with both hands.\",\"Engage your glutes and core muscles, then lift your hips off the ground until your body forms a straight line from your knees to your shoulders.\",\"Pause for a moment at the top, squeezing your glutes.\",\"Slowly lower your hips back down to the starting position.\",\"Repeat for the desired number of repetitions.\"]', 'en', 9, 3, 9, '2024-02-19 19:12:43', '2024-02-19 19:12:47', 125),
(126, 'barbell glute bridge two legs on bench (male)', '126.gif', 'hamstrings,core', '[\"Start by sitting on the edge of a bench with your upper back resting against it and your feet flat on the ground, hip-width apart.\",\"Place a barbell across your hips, holding it securely with both hands.\",\"Engage your glutes and core muscles, then press through your heels to lift your hips off the bench, creating a straight line from your knees to your shoulders.\",\"Pause for a moment at the top, squeezing your glutes.\",\"Slowly lower your hips back down to the starting position.\",\"Repeat for the desired number of repetitions.\"]', 'en', 9, 3, 9, '2024-02-19 19:12:47', '2024-02-19 19:12:51', 126),
(127, 'barbell good morning', '127.gif', 'lower back', '[\"Start by standing with your feet shoulder-width apart and the barbell resting on your upper back.\",\"Keeping your back straight and your core engaged, hinge forward at the hips, pushing your buttocks back as if you were trying to touch the wall behind you with your glutes.\",\"Lower your torso until it is parallel to the ground, feeling a stretch in your hamstrings.\",\"Pause for a moment, then return to the starting position by squeezing your glutes and pushing your hips forward.\",\"Repeat for the desired number of repetitions.\"]', 'en', 9, 3, 10, '2024-02-19 19:12:51', '2024-02-19 19:12:53', 127),
(128, 'barbell guillotine bench press', '128.gif', 'shoulders,triceps', '[\"Lie flat on a bench with your feet flat on the ground and your back pressed against the bench.\",\"Grasp the barbell with an overhand grip, slightly wider than shoulder-width apart.\",\"Lower the barbell slowly towards your neck, keeping your elbows pointed outwards.\",\"Pause for a moment when the barbell is just above your neck.\",\"Push the barbell back up to the starting position, fully extending your arms.\",\"Repeat for the desired number of repetitions.\"]', 'en', 3, 3, 13, '2024-02-19 19:12:53', '2024-02-19 19:12:55', 128),
(129, 'barbell hack squat', '129.gif', 'quadriceps,hamstrings,calves', '[\"Start by standing with your feet shoulder-width apart and your toes slightly turned out.\",\"Hold the barbell behind your legs, resting it on your upper thighs.\",\"Lower your body by bending at the knees and hips, keeping your back straight and your chest up.\",\"Continue lowering until your thighs are parallel to the ground, or as low as you can comfortably go.\",\"Pause for a moment, then push through your heels to return to the starting position.\",\"Repeat for the desired number of repetitions.\"]', 'en', 9, 3, 9, '2024-02-19 19:12:55', '2024-02-19 19:12:59', 129),
(130, 'barbell high bar squat', '130.gif', 'quadriceps,hamstrings,calves,core', '[\"Stand with your feet shoulder-width apart, toes slightly turned out.\",\"Place the barbell on your upper back, resting it on your traps.\",\"Engage your core and keep your chest up as you begin to squat down, pushing your hips back and bending your knees.\",\"Lower yourself until your thighs are parallel to the ground, or as low as you can comfortably go.\",\"Drive through your heels to stand back up, extending your hips and knees.\",\"Repeat for the desired number of repetitions.\"]', 'en', 9, 3, 9, '2024-02-19 19:12:59', '2024-02-19 19:13:02', 130),
(131, 'barbell incline bench press', '131.gif', 'shoulders,triceps', '[\"Set up an incline bench at a 45-degree angle.\",\"Lie down on the bench with your feet flat on the ground.\",\"Grasp the barbell with an overhand grip, slightly wider than shoulder-width apart.\",\"Unrack the barbell and lower it slowly towards your chest, keeping your elbows at a 45-degree angle.\",\"Pause for a moment at the bottom, then push the barbell back up to the starting position.\",\"Repeat for the desired number of repetitions.\"]', 'en', 3, 3, 13, '2024-02-19 19:13:02', '2024-02-19 19:13:05', 131),
(132, 'barbell incline close grip bench press', '132.gif', 'chest,shoulders', '[\"Set up an incline bench at a 45-degree angle.\",\"Lie down on the bench with your feet flat on the ground.\",\"Grasp the barbell with a close grip, slightly narrower than shoulder-width apart.\",\"Unrack the barbell and lower it slowly towards your chest, keeping your elbows close to your body.\",\"Pause for a moment when the barbell touches your chest.\",\"Push the barbell back up to the starting position, fully extending your arms.\",\"Repeat for the desired number of repetitions.\"]', 'en', 8, 3, 18, '2024-02-19 19:13:05', '2024-02-19 19:13:08', 132),
(133, 'barbell incline reverse-grip press', '133.gif', 'chest,shoulders', '[\"Set up an incline bench at a 45-degree angle.\",\"Lie back on the bench and grasp the barbell with a reverse grip, hands slightly wider than shoulder-width apart.\",\"Unrack the barbell and lower it towards your upper chest, keeping your elbows tucked in.\",\"Pause for a moment at the bottom, then push the barbell back up to the starting position.\",\"Repeat for the desired number of repetitions.\"]', 'en', 8, 3, 18, '2024-02-19 19:13:08', '2024-02-19 19:13:10', 133),
(134, 'barbell incline row', '134.gif', 'biceps,forearms', '[\"Set up an incline bench at a 45-degree angle.\",\"Lie face down on the bench with your chest against the pad and your feet flat on the ground.\",\"Grasp the barbell with an overhand grip, slightly wider than shoulder-width apart.\",\"Keep your back straight and your core engaged.\",\"Pull the barbell towards your chest, squeezing your shoulder blades together.\",\"Pause for a moment at the top, then slowly lower the barbell back to the starting position.\",\"Repeat for the desired number of repetitions.\"]', 'en', 1, 3, 19, '2024-02-19 19:13:10', '2024-02-19 19:13:14', 134),
(135, 'barbell incline shoulder raise', '135.gif', 'deltoids,trapezius', '[\"Set up an incline bench at a 45-degree angle.\",\"Sit on the bench with your back against the pad and feet flat on the ground.\",\"Hold a barbell with an overhand grip, slightly wider than shoulder-width apart.\",\"Lift the barbell up to shoulder height, keeping your elbows slightly bent.\",\"Slowly raise the barbell overhead, extending your arms fully.\",\"Pause for a moment at the top, then slowly lower the barbell back to shoulder height.\",\"Repeat for the desired number of repetitions.\"]', 'en', 3, 3, 15, '2024-02-19 19:13:14', '2024-02-19 19:13:18', 135),
(136, 'barbell jefferson squat', '136.gif', 'quadriceps,hamstrings,calves', '[\"Stand with your feet shoulder-width apart and toes slightly turned out.\",\"Hold the barbell with an overhand grip, resting it on the front of your body, just below your waist.\",\"Step your left foot forward and your right foot back, keeping your feet shoulder-width apart.\",\"Bend your knees and lower your body down into a squat position, keeping your back straight and chest up.\",\"Push through your heels to stand back up to the starting position.\",\"Repeat the movement, alternating your forward and back foot with each repetition.\"]', 'en', 9, 3, 9, '2024-02-19 19:13:18', '2024-02-19 19:13:22', 136),
(137, 'barbell jm bench press', '137.gif', 'chest,shoulders', '[\"Lie flat on a bench with your feet flat on the ground and your back pressed against the bench.\",\"Grasp the barbell with an overhand grip, slightly wider than shoulder-width apart.\",\"Lower the barbell to your chest, keeping your elbows tucked in close to your body.\",\"Push the barbell back up to the starting position, fully extending your arms.\",\"Repeat for the desired number of repetitions.\"]', 'en', 8, 3, 18, '2024-02-19 19:13:22', '2024-02-19 19:13:25', 137),
(138, 'barbell jump squat', '138.gif', 'quadriceps,hamstrings,calves', '[\"Start by standing with your feet shoulder-width apart, holding a barbell across your upper back.\",\"Lower your body into a squat position by bending your knees and pushing your hips back.\",\"Once you reach the bottom of the squat, explode upwards by jumping off the ground.\",\"As you jump, extend your hips, knees, and ankles, pushing through your toes.\",\"Land softly back into the squat position and immediately repeat the movement for the desired number of repetitions.\"]', 'en', 9, 3, 9, '2024-02-19 19:13:25', '2024-02-19 19:13:28', 138),
(139, 'barbell lateral lunge', '139.gif', 'quadriceps,hamstrings,calves', '[\"Stand with your feet shoulder-width apart, holding a barbell across your upper back.\",\"Take a big step to the side with your right foot, keeping your left foot planted.\",\"Bend your right knee and lower your body down into a lunge position, keeping your left leg straight.\",\"Push off with your right foot and return to the starting position.\",\"Repeat on the other side, stepping with your left foot.\"]', 'en', 9, 3, 9, '2024-02-19 19:13:28', '2024-02-19 19:13:32', 139),
(140, 'barbell low bar squat', '140.gif', 'quadriceps,hamstrings,calves', '[\"Stand with your feet shoulder-width apart and the barbell resting on your upper back.\",\"Keeping your chest up and core engaged, slowly lower your body by bending your knees and pushing your hips back.\",\"Continue lowering until your thighs are parallel to the ground or slightly below.\",\"Pause for a moment, then push through your heels to return to the starting position.\",\"Repeat for the desired number of repetitions.\"]', 'en', 9, 3, 9, '2024-02-19 19:13:32', '2024-02-19 19:13:36', 140),
(141, 'barbell lunge', '141.gif', 'quadriceps,hamstrings,calves', '[\"Start by standing with your feet shoulder-width apart and a barbell resting on your upper back.\",\"Take a step forward with your right foot, keeping your torso upright.\",\"Lower your body by bending your right knee until your thigh is parallel to the ground.\",\"Push through your right heel to return to the starting position.\",\"Repeat with your left leg, alternating legs for the desired number of repetitions.\"]', 'en', 9, 3, 9, '2024-02-19 19:13:36', '2024-02-19 19:13:38', 141),
(142, 'barbell lying back of the head tricep extension', '142.gif', 'shoulders', '[\"Lie flat on a bench with your feet flat on the ground and your head at the end of the bench.\",\"Hold a barbell with an overhand grip, hands shoulder-width apart, and extend your arms straight up over your chest.\",\"Keeping your upper arms stationary, slowly lower the barbell behind your head by bending your elbows.\",\"Pause for a moment, then extend your arms back up to the starting position.\",\"Repeat for the desired number of repetitions.\"]', 'en', 8, 3, 18, '2024-02-19 19:13:38', '2024-02-19 19:13:41', 142),
(143, 'barbell lying close-grip press', '143.gif', 'chest,shoulders', '[\"Lie flat on a bench with your feet flat on the ground and your back pressed against the bench.\",\"Grasp the barbell with a close grip, hands shoulder-width apart, palms facing towards your feet.\",\"Lift the barbell off the rack and hold it directly above your chest with your arms fully extended.\",\"Slowly lower the barbell towards your chest, keeping your elbows close to your body.\",\"Pause for a moment when the barbell touches your chest, then push it back up to the starting position.\",\"Repeat for the desired number of repetitions.\"]', 'en', 8, 3, 18, '2024-02-19 19:13:41', '2024-02-19 19:13:43', 143),
(144, 'barbell lying close-grip triceps extension', '144.gif', 'shoulders', '[\"Lie flat on a bench with your feet flat on the ground and your head at the end of the bench.\",\"Grasp the barbell with a close grip, hands shoulder-width apart, palms facing up.\",\"Extend your arms fully, lifting the barbell above your chest.\",\"Keeping your upper arms stationary, slowly lower the barbell towards your forehead by bending your elbows.\",\"Pause for a moment at the bottom, then extend your arms back to the starting position.\",\"Repeat for the desired number of repetitions.\"]', 'en', 8, 3, 18, '2024-02-19 19:13:43', '2024-02-19 19:13:47', 144),
(145, 'barbell lying extension', '145.gif', 'shoulders', '[\"Lie flat on a bench with your feet flat on the ground and your head at the end of the bench.\",\"Hold the barbell with an overhand grip, hands shoulder-width apart, and extend your arms straight up over your chest.\",\"Keeping your upper arms stationary, slowly lower the barbell towards your forehead by bending your elbows.\",\"Pause for a moment, then extend your arms back up to the starting position.\",\"Repeat for the desired number of repetitions.\"]', 'en', 8, 3, 18, '2024-02-19 19:13:47', '2024-02-19 19:13:49', 145),
(146, 'barbell lying lifting (on hip)', '146.gif', 'hamstrings,quadriceps', '[\"Lie flat on your back on a bench with your feet flat on the ground and your knees bent.\",\"Hold the barbell with an overhand grip and position it on your hips.\",\"Engaging your glutes, lift your hips off the bench until your body forms a straight line from your knees to your shoulders.\",\"Pause for a moment at the top, then slowly lower your hips back down to the starting position.\",\"Repeat for the desired number of repetitions.\"]', 'en', 9, 3, 9, '2024-02-19 19:13:49', '2024-02-19 19:13:52', 146),
(147, 'barbell lying preacher curl', '147.gif', 'forearms', '[\"Sit on a preacher bench with your chest against the pad and your arms extended over the edge, holding a barbell with an underhand grip.\",\"Keeping your upper arms stationary, exhale and curl the weights while contracting your biceps.\",\"Continue to raise the bar until your biceps are fully contracted and the bar is at shoulder level.\",\"Hold the contracted position for a brief pause as you squeeze your biceps.\",\"Inhale and slowly begin to lower the barbell back to the starting position.\",\"Repeat for the desired number of repetitions.\"]', 'en', 8, 3, 4, '2024-02-19 19:13:52', '2024-02-19 19:13:55', 147),
(148, 'barbell lying triceps extension', '148.gif', 'shoulders', '[\"Lie flat on a bench with your feet flat on the ground and your head at the end of the bench.\",\"Hold the barbell with an overhand grip, hands shoulder-width apart, and extend your arms straight up over your chest.\",\"Keeping your upper arms stationary, slowly lower the barbell towards your forehead by bending your elbows.\",\"Pause for a moment at the bottom, then extend your arms back up to the starting position.\",\"Repeat for the desired number of repetitions.\"]', 'en', 8, 3, 18, '2024-02-19 19:13:55', '2024-02-19 19:13:59', 148),
(149, 'barbell lying triceps extension skull crusher', '149.gif', 'shoulders', '[\"Lie flat on a bench with your feet flat on the ground and your head at the end of the bench.\",\"Hold the barbell with an overhand grip, hands shoulder-width apart, and extend your arms straight up over your chest.\",\"Keeping your upper arms stationary, slowly lower the barbell towards your forehead by bending your elbows.\",\"Pause for a moment when the barbell is just above your forehead, then extend your arms back up to the starting position.\",\"Repeat for the desired number of repetitions.\"]', 'en', 8, 3, 18, '2024-02-19 19:13:59', '2024-02-19 19:14:01', 149),
(150, 'barbell narrow stance squat', '150.gif', 'quadriceps,hamstrings,calves', '[\"Stand with your feet shoulder-width apart and toes pointing slightly outward.\",\"Hold the barbell across your upper back, resting it on your traps or rear delts.\",\"Engage your core and keep your chest up as you slowly lower your body by bending your knees and pushing your hips back.\",\"Continue lowering until your thighs are parallel to the ground or slightly below.\",\"Pause for a moment, then push through your heels to return to the starting position.\",\"Repeat for the desired number of repetitions.\"]', 'en', 9, 3, 9, '2024-02-19 19:14:01', '2024-02-19 19:14:05', 150),
(151, 'barbell one arm bent over row', '151.gif', 'biceps,forearms', '[\"Stand with your feet shoulder-width apart, knees slightly bent, and hold a barbell with one hand using an overhand grip.\",\"Bend forward at the hips, keeping your back straight and your head in a neutral position.\",\"Pull the barbell up towards your chest, keeping your elbow close to your body and squeezing your shoulder blades together.\",\"Lower the barbell back down to the starting position in a controlled manner.\",\"Repeat for the desired number of repetitions, then switch to the other arm.\"]', 'en', 1, 3, 19, '2024-02-19 19:14:05', '2024-02-19 19:14:09', 151),
(152, 'barbell one arm floor press', '152.gif', 'chest,shoulders', '[\"Lie flat on your back on the floor with your knees bent and feet flat on the ground.\",\"Hold the barbell with one hand, palm facing up, and extend your arm straight up over your chest.\",\"Slowly lower the barbell towards your chest, keeping your elbow close to your body.\",\"Pause for a moment at the bottom, then push the barbell back up to the starting position.\",\"Repeat for the desired number of repetitions, then switch arms.\"]', 'en', 8, 3, 18, '2024-02-19 19:14:09', '2024-02-19 19:14:13', 152),
(153, 'barbell one arm side deadlift', '153.gif', 'hamstrings,quadriceps,lower back', '[\"Stand with your feet shoulder-width apart, holding a barbell in one hand with an overhand grip.\",\"Keep your back straight and your core engaged.\",\"Bend at the hips and lower the barbell towards the outside of your leg, keeping your arm straight and your chest up.\",\"Lower the barbell as far as you can while maintaining good form.\",\"Pause for a moment, then slowly return to the starting position.\",\"Repeat for the desired number of repetitions, then switch sides.\"]', 'en', 9, 3, 9, '2024-02-19 19:14:13', '2024-02-19 19:14:17', 153),
(154, 'barbell one arm snatch', '154.gif', 'traps,forearms,core', '[\"Stand with your feet shoulder-width apart, toes pointing slightly outwards.\",\"Hold the barbell with an overhand grip, hands slightly wider than shoulder-width apart.\",\"Bend your knees and lower your hips into a squat position, keeping your back straight and chest up.\",\"Explosively extend your hips, knees, and ankles, driving the barbell upwards.\",\"As the barbell reaches chest level, pull it upwards with your arm, keeping it close to your body.\",\"Rotate your elbow under the barbell and extend your arm fully overhead, locking out your elbow.\",\"Lower the barbell back down to the starting position in a controlled manner.\",\"Repeat for the desired number of repetitions, then switch arms.\"]', 'en', 7, 3, 7, '2024-02-19 19:14:18', '2024-02-19 19:14:20', 154),
(155, 'barbell one leg squat', '155.gif', 'glutes,hamstrings,calves', '[\"Stand with your feet shoulder-width apart and hold a barbell across your upper back.\",\"Lift one foot off the ground and extend it forward, keeping it parallel to the ground.\",\"Bend your standing leg and lower your body down as if sitting back into a chair, keeping your chest up and your back straight.\",\"Lower yourself until your thigh is parallel to the ground, then push through your heel to return to the starting position.\",\"Repeat for the desired number of repetitions, then switch legs and repeat.\"]', 'en', 9, 3, 14, '2024-02-19 19:14:20', '2024-02-19 19:14:22', 155),
(156, 'barbell overhead squat', '156.gif', 'glutes,hamstrings,calves,core', '[\"Stand with your feet shoulder-width apart and toes slightly turned out.\",\"Hold the barbell with a wide grip, positioning it overhead with your arms fully extended.\",\"Engage your core and lower your body down into a squat position, keeping your chest up and knees tracking over your toes.\",\"Pause for a moment at the bottom, then push through your heels to return to the starting position.\",\"Repeat for the desired number of repetitions.\"]', 'en', 9, 3, 14, '2024-02-19 19:14:22', '2024-02-19 19:14:25', 156),
(157, 'barbell palms down wrist curl over a bench', '157.gif', 'biceps,brachialis', '[\"Sit on a bench with your feet flat on the ground and your forearms resting on your thighs, palms facing down.\",\"Hold a barbell with an overhand grip, hands shoulder-width apart.\",\"Lower the barbell towards the ground by flexing your wrists, keeping your forearms stationary.\",\"Pause for a moment at the bottom, then slowly raise the barbell back up by extending your wrists.\",\"Repeat for the desired number of repetitions.\"]', 'en', 4, 3, 8, '2024-02-19 19:14:25', '2024-02-19 19:14:28', 157),
(158, 'barbell palms up wrist curl over a bench', '158.gif', 'biceps,shoulders', '[\"Sit on a bench with your feet flat on the ground and hold a barbell with an underhand grip, palms facing up.\",\"Rest your forearms on the bench, allowing your wrists to hang off the edge.\",\"Keeping your forearms stationary, exhale and curl your wrists upwards as far as possible.\",\"Hold the contracted position for a brief pause, then inhale and slowly lower the barbell back to the starting position.\",\"Repeat for the desired number of repetitions.\"]', 'en', 4, 3, 8, '2024-02-19 19:14:28', '2024-02-19 19:14:31', 158),
(159, 'barbell pendlay row', '159.gif', 'biceps,forearms', '[\"Stand with your feet shoulder-width apart and your knees slightly bent.\",\"Bend forward at the hips, keeping your back straight and your chest up.\",\"Grasp the barbell with an overhand grip, slightly wider than shoulder-width apart.\",\"Pull the barbell towards your upper abdomen, squeezing your shoulder blades together.\",\"Lower the barbell back down to the starting position.\",\"Repeat for the desired number of repetitions.\"]', 'en', 1, 3, 19, '2024-02-19 19:14:31', '2024-02-19 19:14:35', 159),
(160, 'barbell pin presses', '160.gif', 'shoulders', '[\"Set up a barbell on a power rack at chest height.\",\"Stand facing the barbell and position yourself underneath it, with your feet shoulder-width apart.\",\"Grip the barbell with an overhand grip, slightly wider than shoulder-width apart.\",\"Lift the barbell off the rack and hold it directly above your chest, with your arms fully extended.\",\"Lower the barbell down towards your chest, keeping your elbows tucked in close to your body.\",\"Pause for a moment when the barbell touches your chest.\",\"Push the barbell back up to the starting position, fully extending your arms.\",\"Repeat for the desired number of repetitions.\"]', 'en', 8, 3, 18, '2024-02-19 19:14:35', '2024-02-19 19:14:39', 160),
(161, 'barbell preacher curl', '161.gif', 'forearms', '[\"Sit on a preacher bench with your upper arms resting on the pad and your chest against the support.\",\"Grasp the barbell with an underhand grip, slightly wider than shoulder-width apart.\",\"Keeping your upper arms stationary, exhale and curl the barbell up towards your shoulders.\",\"Pause for a moment at the top, squeezing your biceps.\",\"Inhale and slowly lower the barbell back down to the starting position.\",\"Repeat for the desired number of repetitions.\"]', 'en', 8, 3, 4, '2024-02-19 19:14:39', '2024-02-19 19:14:42', 161);
INSERT INTO `exercises` (`id`, `name`, `gif`, `secondary_muscles`, `instructions`, `lang`, `body_part_id`, `equipment_id`, `target_id`, `created_at`, `updated_at`, `exercise_id`) VALUES
(162, 'barbell press sit-up', '162.gif', 'shoulders,chest', '[\"Lie flat on your back on a mat with your knees bent and feet flat on the ground.\",\"Hold the barbell with an overhand grip, resting it on your chest.\",\"Engaging your abs, slowly lift your upper body off the ground, curling forward until your torso is at a 45-degree angle.\",\"Pause for a moment at the top, then slowly lower your upper body back down to the starting position.\",\"Repeat for the desired number of repetitions.\"]', 'en', 10, 3, 2, '2024-02-19 19:14:42', '2024-02-19 19:14:44', 162),
(163, 'barbell prone incline curl', '163.gif', 'forearms', '[\"Set up an incline bench at a 45-degree angle.\",\"Lie face down on the bench with your chest and stomach resting against it.\",\"Hold a barbell with an underhand grip, shoulder-width apart.\",\"Extend your arms fully, allowing the barbell to hang down towards the floor.\",\"Keeping your upper arms stationary, exhale and curl the weights while contracting your biceps.\",\"Continue to raise the barbell until your biceps are fully contracted and the bar is at shoulder level.\",\"Hold the contracted position for a brief pause as you squeeze your biceps.\",\"Inhale and slowly begin to lower the barbell back to the starting position.\",\"Repeat for the desired number of repetitions.\"]', 'en', 8, 3, 4, '2024-02-19 19:14:44', '2024-02-19 19:14:46', 163),
(164, 'barbell pullover', '164.gif', 'chest,triceps', '[\"Lie flat on a bench with your head at one end and your feet on the floor.\",\"Hold a barbell with a shoulder-width grip and extend your arms straight above your chest.\",\"Keeping your arms straight, lower the barbell behind your head in a controlled manner until you feel a stretch in your lats.\",\"Pause for a moment, then raise the barbell back to the starting position.\",\"Repeat for the desired number of repetitions.\"]', 'en', 1, 3, 11, '2024-02-19 19:14:46', '2024-02-19 19:14:49', 164),
(165, 'barbell pullover to press', '165.gif', 'triceps,chest,shoulders', '[\"Lie flat on a bench with your head at one end and your feet on the ground.\",\"Hold the barbell with a pronated grip (palms facing away from you) and extend your arms straight above your chest.\",\"Keeping your arms straight, lower the barbell behind your head in an arc-like motion until you feel a stretch in your lats.\",\"Pause for a moment, then reverse the motion and press the barbell back to the starting position above your chest.\",\"Repeat for the desired number of repetitions.\"]', 'en', 1, 3, 11, '2024-02-19 19:14:49', '2024-02-19 19:14:51', 165),
(166, 'barbell rack pull', '166.gif', 'hamstrings,lower back', '[\"Set up a barbell on a rack at knee height.\",\"Stand with your feet shoulder-width apart, toes pointing slightly outwards.\",\"Bend at the hips and knees to lower yourself down and grip the barbell with an overhand grip, hands shoulder-width apart.\",\"Engage your core and lift the barbell by extending your hips and knees, pulling your shoulders back and squeezing your glutes at the top.\",\"Lower the barbell back down to the starting position by bending at the hips and knees.\",\"Repeat for the desired number of repetitions.\"]', 'en', 9, 3, 9, '2024-02-19 19:14:51', '2024-02-19 19:14:53', 166),
(167, 'barbell rear delt raise', '167.gif', 'traps,rhomboids', '[\"Stand with your feet shoulder-width apart and hold a barbell with an overhand grip, palms facing down.\",\"Bend your knees slightly and hinge forward at the hips, keeping your back straight.\",\"Raise the barbell out to the sides, keeping your arms straight, until they are parallel to the ground.\",\"Pause for a moment at the top, then slowly lower the barbell back to the starting position.\",\"Repeat for the desired number of repetitions.\"]', 'en', 7, 3, 7, '2024-02-19 19:14:53', '2024-02-19 19:14:56', 167),
(168, 'barbell rear delt row', '168.gif', 'trapezius,rhomboids,biceps', '[\"Stand with your feet shoulder-width apart and knees slightly bent.\",\"Hold a barbell with an overhand grip, hands slightly wider than shoulder-width apart.\",\"Bend forward at the hips, keeping your back straight and chest up.\",\"Pull the barbell towards your chest, squeezing your shoulder blades together.\",\"Pause for a moment at the top, then slowly lower the barbell back to the starting position.\",\"Repeat for the desired number of repetitions.\"]', 'en', 7, 3, 7, '2024-02-19 19:14:56', '2024-02-19 19:15:00', 168),
(169, 'barbell rear lunge', '169.gif', 'quadriceps,hamstrings,calves', '[\"Start by standing with your feet shoulder-width apart and a barbell resting on your upper back.\",\"Take a step backward with your right foot, landing on the ball of your foot.\",\"Bend both knees to lower your body until your left thigh is parallel to the ground.\",\"Push through your left heel to return to the starting position.\",\"Repeat with the other leg.\"]', 'en', 9, 3, 9, '2024-02-19 19:15:00', '2024-02-19 19:15:02', 169),
(170, 'barbell rear lunge v. 2', '170.gif', 'quadriceps,hamstrings,calves', '[\"Stand with your feet shoulder-width apart and hold a barbell across your upper back.\",\"Take a step backward with your right foot, landing on the ball of your foot.\",\"Bend both knees to lower your body until your left thigh is parallel to the ground.\",\"Push through your left heel to return to the starting position.\",\"Repeat with the other leg.\"]', 'en', 9, 3, 9, '2024-02-19 19:15:02', '2024-02-19 19:15:06', 170),
(171, 'barbell revers wrist curl v. 2', '171.gif', 'biceps,brachialis', '[\"Sit on a bench with your feet flat on the ground and your knees bent.\",\"Hold a barbell with an overhand grip, palms facing down, and your hands shoulder-width apart.\",\"Rest your forearms on your thighs, allowing your wrists to hang off the edge.\",\"Keeping your forearms stationary, exhale and curl your wrists upward as far as possible.\",\"Hold the contracted position for a brief pause, then inhale and slowly lower the barbell back to the starting position.\",\"Repeat for the desired number of repetitions.\"]', 'en', 4, 3, 8, '2024-02-19 19:15:06', '2024-02-19 19:15:09', 171),
(172, 'barbell reverse close-grip bench press', '172.gif', 'chest,shoulders', '[\"Lie flat on a bench with your feet flat on the ground and your back pressed against the bench.\",\"Grasp the barbell with a reverse grip, hands shoulder-width apart.\",\"Lift the barbell off the rack and hold it directly above your chest with your arms fully extended.\",\"Slowly lower the barbell down towards your chest, keeping your elbows close to your body.\",\"Pause for a moment when the barbell is just above your chest.\",\"Push the barbell back up to the starting position, fully extending your arms.\",\"Repeat for the desired number of repetitions.\"]', 'en', 8, 3, 18, '2024-02-19 19:15:09', '2024-02-19 19:15:13', 172),
(173, 'barbell reverse curl', '173.gif', 'forearms', '[\"Stand up straight with your feet shoulder-width apart and hold a barbell with an overhand grip, palms facing down.\",\"Keep your upper arms stationary and exhale as you curl the barbell upward, contracting your biceps.\",\"Continue to raise the barbell until your biceps are fully contracted and the barbell is at shoulder level.\",\"Hold the contracted position for a brief pause as you squeeze your biceps.\",\"Inhale as you slowly lower the barbell back to the starting position, keeping your upper arms stationary.\",\"Repeat for the desired number of repetitions.\"]', 'en', 8, 3, 4, '2024-02-19 19:15:13', '2024-02-19 19:15:15', 173),
(174, 'barbell reverse grip bent over row', '174.gif', 'biceps,forearms', '[\"Stand with your feet shoulder-width apart and knees slightly bent.\",\"Hold a barbell with an overhand grip, palms facing down, and hands slightly wider than shoulder-width apart.\",\"Bend forward at the hips, keeping your back straight and chest up, until your torso is almost parallel to the floor.\",\"Pull the barbell towards your lower chest, squeezing your shoulder blades together.\",\"Pause for a moment at the top, then slowly lower the barbell back to the starting position.\",\"Repeat for the desired number of repetitions.\"]', 'en', 1, 3, 19, '2024-02-19 19:15:15', '2024-02-19 19:15:18', 174),
(175, 'barbell reverse grip decline bench press', '175.gif', 'triceps,shoulders', '[\"Lie on a decline bench with your feet secured and your head lower than your hips.\",\"Grasp the barbell with a reverse grip, slightly wider than shoulder-width apart.\",\"Unrack the barbell and lower it slowly towards your chest, keeping your elbows tucked in.\",\"Pause for a moment at the bottom, then push the barbell back up to the starting position.\",\"Repeat for the desired number of repetitions.\"]', 'en', 3, 3, 13, '2024-02-19 19:15:18', '2024-02-19 19:15:22', 175),
(176, 'barbell reverse grip incline bench press', '176.gif', 'triceps,shoulders', '[\"Set up an incline bench at a 45-degree angle.\",\"Lie down on the bench with your feet flat on the ground.\",\"Grasp the barbell with a reverse grip, hands slightly wider than shoulder-width apart.\",\"Unrack the barbell and lower it slowly towards your chest, keeping your elbows tucked in.\",\"Pause for a moment when the barbell touches your chest.\",\"Push the barbell back up to the starting position, fully extending your arms.\",\"Repeat for the desired number of repetitions.\"]', 'en', 3, 3, 13, '2024-02-19 19:15:22', '2024-02-19 19:15:25', 176),
(177, 'barbell reverse grip incline bench row', '177.gif', 'biceps,forearms', '[\"Set up an incline bench at a 45-degree angle.\",\"Sit on the bench facing the backrest with your chest against it.\",\"Grab the barbell with a reverse grip (palms facing down) and hands slightly wider than shoulder-width apart.\",\"Keep your back straight and core engaged.\",\"Pull the barbell towards your upper abdomen, squeezing your shoulder blades together.\",\"Pause for a moment at the top of the movement.\",\"Slowly lower the barbell back to the starting position.\",\"Repeat for the desired number of repetitions.\"]', 'en', 1, 3, 19, '2024-02-19 19:15:25', '2024-02-19 19:15:28', 177),
(178, 'barbell reverse grip skullcrusher', '178.gif', 'forearms', '[\"Lie flat on a bench with your feet flat on the ground and your head at the end of the bench.\",\"Hold the barbell with a reverse grip, palms facing towards your face, and your hands shoulder-width apart.\",\"Extend your arms straight up over your chest, keeping your elbows in and your wrists straight.\",\"Slowly lower the barbell towards your forehead by bending your elbows, keeping your upper arms stationary.\",\"Pause for a moment at the bottom, then extend your arms back up to the starting position.\",\"Repeat for the desired number of repetitions.\"]', 'en', 8, 3, 18, '2024-02-19 19:15:28', '2024-02-19 19:15:32', 178),
(179, 'barbell reverse preacher curl', '179.gif', 'forearms', '[\"Sit on a preacher bench with your chest against the pad and your arms extended straight down, holding a barbell with an overhand grip.\",\"Keeping your upper arms stationary, exhale and curl the barbell upward while contracting your biceps.\",\"Continue to raise the barbell until your biceps are fully contracted and the barbell is at shoulder level.\",\"Hold the contracted position for a brief pause as you squeeze your biceps.\",\"Inhale and slowly lower the barbell back to the starting position.\",\"Repeat for the desired number of repetitions.\"]', 'en', 8, 3, 4, '2024-02-19 19:15:32', '2024-02-19 19:15:35', 179),
(180, 'barbell reverse wrist curl', '180.gif', 'biceps,brachialis', '[\"Sit on a bench with your feet flat on the ground and hold a barbell with an overhand grip, palms facing down.\",\"Rest your forearms on your thighs, allowing your wrists to hang off the edge.\",\"Slowly curl your wrists upward, bringing the barbell towards your body.\",\"Pause for a moment at the top, then slowly lower the barbell back down to the starting position.\",\"Repeat for the desired number of repetitions.\"]', 'en', 4, 3, 8, '2024-02-19 19:15:35', '2024-02-19 19:15:39', 180),
(181, 'barbell rollerout', '181.gif', 'lower back', '[\"Kneel on the floor and hold a barbell with both hands, shoulder-width apart.\",\"Roll the barbell forward, extending your arms and keeping your core engaged.\",\"Continue rolling forward until your body is fully extended and your arms are overhead.\",\"Pause for a moment, then slowly roll the barbell back towards your knees, maintaining control.\",\"Repeat for the desired number of repetitions.\"]', 'en', 10, 3, 2, '2024-02-19 19:15:39', '2024-02-19 19:15:42', 181),
(182, 'barbell rollerout from bench', '182.gif', 'shoulders,triceps', '[\"Start by kneeling on the floor with a barbell placed on a bench in front of you.\",\"Grasp the barbell with an overhand grip, slightly wider than shoulder-width apart.\",\"Keeping your core engaged and your back straight, slowly roll the barbell forward, extending your arms in front of you.\",\"Continue rolling the barbell forward until your body is fully extended and your arms are overhead.\",\"Pause for a moment at the fully extended position, then slowly roll the barbell back towards your body, returning to the starting position.\",\"Repeat for the desired number of repetitions.\"]', 'en', 10, 3, 2, '2024-02-19 19:15:42', '2024-02-19 19:15:44', 182),
(183, 'barbell romanian deadlift', '183.gif', 'hamstrings,lower back', '[\"Stand with your feet shoulder-width apart and your toes pointing forward.\",\"Hold the barbell with an overhand grip, hands slightly wider than shoulder-width apart.\",\"Bend at the hips, keeping your back straight and your knees slightly bent.\",\"Lower the barbell towards the ground, keeping it close to your body.\",\"Feel the stretch in your hamstrings as you lower the barbell.\",\"Once you feel a stretch in your hamstrings, push your hips forward and stand up straight.\",\"Squeeze your glutes at the top of the movement.\",\"Lower the barbell back down to the starting position and repeat for the desired number of repetitions.\"]', 'en', 9, 3, 9, '2024-02-19 19:15:44', '2024-02-19 19:15:47', 183),
(184, 'barbell seated behind head military press', '184.gif', 'triceps,upper back', '[\"Sit on a bench with your back straight and feet flat on the ground.\",\"Hold the barbell with an overhand grip, slightly wider than shoulder-width apart.\",\"Lift the barbell off the rack and bring it down to shoulder level, behind your head.\",\"Press the barbell upward until your arms are fully extended.\",\"Lower the barbell back down to the starting position.\",\"Repeat for the desired number of repetitions.\"]', 'en', 7, 3, 7, '2024-02-19 19:15:47', '2024-02-19 19:15:50', 184),
(185, 'barbell seated bradford rocky press', '185.gif', 'triceps,upper back', '[\"Sit on a bench with your back straight and feet flat on the ground.\",\"Hold the barbell with an overhand grip, slightly wider than shoulder-width apart.\",\"Lift the barbell to shoulder height, keeping your elbows slightly bent and pointing forward.\",\"Press the barbell overhead, fully extending your arms.\",\"Lower the barbell back to shoulder height and repeat for the desired number of repetitions.\"]', 'en', 7, 3, 7, '2024-02-19 19:15:50', '2024-02-19 19:15:54', 185),
(186, 'barbell seated calf raise', '186.gif', 'hamstrings,quadriceps', '[\"Sit on a bench with your feet flat on the floor and a barbell resting on your thighs.\",\"Place the balls of your feet on a raised platform, such as a block or step.\",\"Position the barbell across your thighs and hold it securely with your hands.\",\"Keeping your back straight and your core engaged, lift your heels off the ground by extending your ankles.\",\"Pause for a moment at the top, then slowly lower your heels back down to the starting position.\",\"Repeat for the desired number of repetitions.\"]', 'en', 5, 3, 5, '2024-02-19 19:15:54', '2024-02-19 19:15:58', 186),
(187, 'barbell seated calf raise', '187.gif', 'hamstrings', '[\"Sit on a bench with your feet flat on the floor and a barbell resting on your thighs.\",\"Place the balls of your feet on a raised platform, such as a block or step.\",\"Lower your heels as far as possible, feeling a stretch in your calves.\",\"Raise your heels as high as possible, contracting your calves.\",\"Repeat for the desired number of repetitions.\"]', 'en', 5, 3, 5, '2024-02-19 19:15:58', '2024-02-19 19:16:02', 187),
(188, 'barbell seated close grip behind neck triceps extension', '188.gif', 'shoulders', '[\"Sit on a bench with your back straight and feet flat on the ground.\",\"Hold the barbell with a close grip behind your neck, palms facing forward.\",\"Keep your elbows close to your head and slowly lower the barbell towards the back of your head.\",\"Pause for a moment, then extend your arms back up to the starting position.\",\"Repeat for the desired number of repetitions.\"]', 'en', 8, 3, 18, '2024-02-19 19:16:02', '2024-02-19 19:16:04', 188),
(189, 'barbell seated close-grip concentration curl', '189.gif', 'forearms', '[\"Sit on a bench with your feet flat on the floor and hold a barbell with an underhand grip, hands shoulder-width apart.\",\"Rest your upper arms against your inner thighs, just above your knees, and let the barbell hang down in front of you.\",\"Keeping your upper arms stationary, exhale and curl the barbell up towards your shoulders, contracting your biceps.\",\"Hold the contracted position for a brief pause, then inhale and slowly lower the barbell back down to the starting position.\",\"Repeat for the desired number of repetitions.\"]', 'en', 8, 3, 4, '2024-02-19 19:16:04', '2024-02-19 19:16:07', 189),
(190, 'barbell seated good morning', '190.gif', 'hamstrings,lower back', '[\"Sit on a bench with your feet flat on the ground and a barbell resting on your upper back.\",\"Keep your back straight and your chest up.\",\"Slowly hinge forward at the hips, lowering your torso towards the ground.\",\"Pause for a moment at the bottom, then push through your heels to return to the starting position.\",\"Repeat for the desired number of repetitions.\"]', 'en', 9, 3, 9, '2024-02-19 19:16:07', '2024-02-19 19:16:10', 190),
(191, 'barbell seated overhead press', '191.gif', 'triceps,upper back', '[\"Sit on a bench with your back straight and feet flat on the ground.\",\"Hold the barbell with an overhand grip, slightly wider than shoulder-width apart.\",\"Lift the barbell off the rack and bring it to shoulder level, with your elbows bent and palms facing forward.\",\"Press the barbell overhead by extending your arms fully.\",\"Pause for a moment at the top, then slowly lower the barbell back to shoulder level.\",\"Repeat for the desired number of repetitions.\"]', 'en', 7, 3, 7, '2024-02-19 19:16:10', '2024-02-19 19:16:12', 191),
(192, 'barbell seated overhead triceps extension', '192.gif', 'shoulders', '[\"Sit on a bench with your back straight and feet flat on the ground.\",\"Hold a barbell with an overhand grip, hands shoulder-width apart, and raise it overhead.\",\"Lower the barbell behind your head by bending your elbows, keeping your upper arms close to your head.\",\"Pause for a moment, then extend your arms to raise the barbell back to the starting position.\",\"Repeat for the desired number of repetitions.\"]', 'en', 8, 3, 18, '2024-02-19 19:16:12', '2024-02-19 19:16:16', 192),
(193, 'barbell seated twist', '193.gif', 'obliques,lower back', '[\"Sit on a flat bench with your feet flat on the ground and your knees bent.\",\"Hold a barbell with both hands in front of your chest, keeping your elbows slightly bent.\",\"Engage your core and slowly twist your torso to one side, keeping your back straight.\",\"Pause for a moment at the end of the twist, then slowly rotate back to the starting position.\",\"Repeat the twist to the other side.\",\"Continue alternating sides for the desired number of repetitions.\"]', 'en', 10, 3, 2, '2024-02-19 19:16:16', '2024-02-19 19:16:18', 193),
(194, 'barbell shrug', '194.gif', 'shoulders', '[\"Stand with your feet shoulder-width apart and hold a barbell in front of you with an overhand grip.\",\"Keep your arms straight and your back straight throughout the exercise.\",\"Lift your shoulders up towards your ears as high as possible, squeezing your traps at the top.\",\"Hold for a moment, then slowly lower your shoulders back down to the starting position.\",\"Repeat for the desired number of repetitions.\"]', 'en', 1, 3, 17, '2024-02-19 19:16:18', '2024-02-19 19:16:22', 194),
(195, 'barbell side bent v. 2', '195.gif', 'obliques,lower back', '[\"Stand with your feet shoulder-width apart and hold a barbell with both hands, palms facing down.\",\"Keep your back straight and core engaged throughout the exercise.\",\"Slowly bend your torso to the right side, lowering the barbell towards your right knee.\",\"Pause for a moment, then return to the starting position.\",\"Repeat the movement on the left side.\",\"Continue alternating sides for the desired number of repetitions.\"]', 'en', 10, 3, 2, '2024-02-19 19:16:22', '2024-02-19 19:16:25', 195),
(196, 'barbell side split squat', '196.gif', 'glutes,hamstrings,calves', '[\"Stand with your feet wider than shoulder-width apart, toes pointing slightly outward.\",\"Hold a barbell across your upper back, resting it on your traps.\",\"Engage your core and keep your chest up as you lower your body down into a squat position, bending at the knees and hips.\",\"As you lower, push your knees out to the sides and keep your weight on your heels.\",\"Lower until your thighs are parallel to the ground, then push through your heels to return to the starting position.\",\"Repeat for the desired number of repetitions.\"]', 'en', 9, 3, 14, '2024-02-19 19:16:25', '2024-02-19 19:16:29', 196),
(197, 'barbell side split squat v. 2', '197.gif', 'glutes,hamstrings,calves', '[\"Stand with your feet wider than shoulder-width apart, toes pointing slightly outwards.\",\"Hold a barbell across your upper back, resting it on your shoulders.\",\"Take a big step to the side with your right foot, keeping your left foot planted.\",\"Bend your right knee and lower your body down into a squat position, keeping your chest up and your back straight.\",\"Push through your right heel to return to the starting position.\",\"Repeat on the other side, stepping out with your left foot.\",\"Continue alternating sides for the desired number of repetitions.\"]', 'en', 9, 3, 14, '2024-02-19 19:16:29', '2024-02-19 19:16:33', 197),
(198, 'barbell single leg deadlift', '198.gif', 'hamstrings,lower back', '[\"Stand with your feet hip-width apart, holding a barbell in front of your thighs with an overhand grip.\",\"Shift your weight onto your left foot and lift your right foot slightly off the ground.\",\"Hinge forward at the hips, keeping your back straight and your right leg extended behind you for balance.\",\"Lower the barbell towards the ground, keeping it close to your body and your left leg slightly bent.\",\"Pause for a moment at the bottom, then engage your glutes and hamstrings to lift your torso back up to the starting position.\",\"Repeat for the desired number of repetitions, then switch sides.\"]', 'en', 9, 3, 9, '2024-02-19 19:16:34', '2024-02-19 19:16:36', 198),
(199, 'barbell single leg split squat', '199.gif', 'glutes,hamstrings,calves', '[\"Stand with your feet shoulder-width apart, holding a barbell across your upper back.\",\"Take a large step forward with one leg, keeping your torso upright.\",\"Lower your body by bending your front knee and hip, while keeping your back leg straight.\",\"Continue lowering until your front thigh is parallel to the ground.\",\"Pause for a moment, then push through your front heel to return to the starting position.\",\"Repeat for the desired number of repetitions, then switch legs.\"]', 'en', 9, 3, 14, '2024-02-19 19:16:36', '2024-02-19 19:16:39', 199),
(200, 'barbell sitted alternate leg raise', '200.gif', 'hip flexors', '[\"Sit on a bench with your back straight and hold a barbell across your thighs.\",\"Keeping your legs straight, lift one leg up as high as possible while keeping the other leg on the ground.\",\"Lower the raised leg back down and repeat with the other leg.\",\"Continue alternating legs for the desired number of repetitions.\"]', 'en', 10, 3, 2, '2024-02-19 19:16:39', '2024-02-19 19:16:41', 200),
(201, 'barbell sitted alternate leg raise (female)', '201.gif', 'hip flexors,quadriceps', '[\"Sit on a bench with your back straight and hold a barbell across your thighs.\",\"Place your hands on the sides of the bench for support.\",\"Keeping your legs straight, lift one leg up as high as possible while keeping it parallel to the ground.\",\"Lower the leg back down and repeat with the other leg.\",\"Continue alternating legs for the desired number of repetitions.\"]', 'en', 10, 3, 2, '2024-02-19 19:16:41', '2024-02-19 19:16:44', 201),
(202, 'barbell skier', '202.gif', 'triceps,core', '[\"Stand with your feet shoulder-width apart and hold a barbell in front of your thighs with an overhand grip.\",\"Bend your knees slightly and hinge forward at the hips, keeping your back straight and chest up.\",\"Simultaneously lift the barbell up towards your shoulders while jumping slightly off the ground.\",\"As you reach the top of the movement, quickly reverse the motion and lower the barbell back down to the starting position.\",\"Repeat for the desired number of repetitions.\"]', 'en', 7, 3, 7, '2024-02-19 19:16:44', '2024-02-19 19:16:49', 202),
(203, 'barbell speed squat', '203.gif', 'quadriceps,hamstrings,calves', '[\"Stand with your feet shoulder-width apart, toes slightly turned out.\",\"Hold the barbell across your upper back, resting it on your traps or rear delts.\",\"Engage your core and keep your chest up as you lower your hips back and down, as if sitting into a chair.\",\"Lower until your thighs are parallel to the ground, or as low as you can comfortably go.\",\"Drive through your heels to stand back up, squeezing your glutes at the top.\",\"Repeat for the desired number of repetitions.\"]', 'en', 9, 3, 9, '2024-02-19 19:16:49', '2024-02-19 19:16:51', 203),
(204, 'barbell split squat v. 2', '204.gif', 'glutes,hamstrings,calves', '[\"Start by standing with your feet shoulder-width apart, holding a barbell across your upper back.\",\"Take a large step forward with your right foot, keeping your torso upright.\",\"Lower your body by bending your knees and hips until your right thigh is parallel to the ground.\",\"Pause for a moment, then push through your right heel to return to the starting position.\",\"Repeat with your left leg forward for the desired number of repetitions.\"]', 'en', 9, 3, 14, '2024-02-19 19:16:51', '2024-02-19 19:16:54', 204),
(205, 'barbell squat (on knees)', '205.gif', 'glutes,hamstrings,calves', '[\"Start by kneeling on the ground with your knees hip-width apart and your toes pointing forward.\",\"Place a barbell across your shoulders, gripping it with an overhand grip and your hands slightly wider than shoulder-width apart.\",\"Engage your core and keep your chest lifted as you slowly lower your body down by bending your knees, keeping your back straight.\",\"Continue lowering until your thighs are parallel to the ground, or as low as you can comfortably go.\",\"Pause for a moment at the bottom, then push through your heels to return to the starting position.\",\"Repeat for the desired number of repetitions.\"]', 'en', 9, 3, 14, '2024-02-19 19:16:54', '2024-02-19 19:16:58', 205),
(206, 'barbell squat jump step rear lunge', '206.gif', 'glutes,hamstrings,calves', '[\"Start by standing with your feet shoulder-width apart, holding a barbell across your upper back.\",\"Lower your body into a squat position by bending your knees and pushing your hips back.\",\"Explode upwards, jumping off the ground as high as you can.\",\"Land softly on your feet and immediately step back with one leg into a reverse lunge.\",\"Lower your body down until your front thigh is parallel to the ground, keeping your back straight.\",\"Push through your front heel to return to the starting position.\",\"Repeat the jump and lunge sequence on the other leg.\",\"Continue alternating legs for the desired number of repetitions.\"]', 'en', 9, 3, 14, '2024-02-19 19:16:58', '2024-02-19 19:17:02', 206),
(207, 'barbell standing ab rollerout', '207.gif', 'shoulders,lower back', '[\"Stand upright with your feet shoulder-width apart and hold the barbell with both hands in front of your thighs.\",\"Engage your core and slowly roll the barbell down towards the ground, keeping your back straight and your arms extended.\",\"Continue rolling the barbell forward until your body is fully extended and your hands are directly above your head.\",\"Pause for a moment, then slowly roll the barbell back towards your thighs, maintaining control and keeping your core engaged.\",\"Repeat for the desired number of repetitions.\"]', 'en', 10, 3, 2, '2024-02-19 19:17:02', '2024-02-19 19:17:06', 207),
(208, 'barbell standing back wrist curl', '208.gif', 'biceps,shoulders', '[\"Stand up straight with your feet shoulder-width apart and hold a barbell with an overhand grip.\",\"Rest the barbell on the back of your hands with your palms facing down and your fingers pointing towards your body.\",\"Keeping your upper arms stationary, exhale and curl your wrists upwards as far as possible.\",\"Hold the contracted position for a brief pause, then inhale and slowly lower the barbell back to the starting position.\",\"Repeat for the desired number of repetitions.\"]', 'en', 4, 3, 8, '2024-02-19 19:17:06', '2024-02-19 19:17:08', 208),
(209, 'barbell standing bradford press', '209.gif', 'triceps,upper back', '[\"Stand with your feet shoulder-width apart and hold the barbell in front of your shoulders with an overhand grip.\",\"Press the barbell overhead, fully extending your arms.\",\"Lower the barbell back down to the starting position.\",\"Repeat for the desired number of repetitions.\"]', 'en', 7, 3, 7, '2024-02-19 19:17:08', '2024-02-19 19:17:12', 209),
(210, 'barbell standing calf raise', '210.gif', 'hamstrings,glutes', '[\"Stand with your feet shoulder-width apart and place a barbell across your upper back.\",\"Raise your heels off the ground as high as possible, using only your toes.\",\"Pause for a moment at the top, then slowly lower your heels back down to the starting position.\",\"Repeat for the desired number of repetitions.\"]', 'en', 5, 3, 5, '2024-02-19 19:17:12', '2024-02-19 19:17:15', 210),
(211, 'barbell standing close grip curl', '211.gif', 'forearms', '[\"Stand up straight with your feet shoulder-width apart and hold a barbell with an underhand grip, hands close together.\",\"Keep your elbows close to your torso and your upper arms stationary throughout the movement.\",\"Exhale as you curl the weights while contracting your biceps. Continue to raise the bar until your biceps are fully contracted and the bar is at shoulder level.\",\"Hold the contracted position for a brief pause as you squeeze your biceps.\",\"Inhale as you slowly begin to bring the barbell back to the starting position.\",\"Repeat for the desired number of repetitions.\"]', 'en', 8, 3, 4, '2024-02-19 19:17:15', '2024-02-19 19:17:17', 211),
(212, 'barbell standing close grip military press', '212.gif', 'triceps,upper back', '[\"Stand with your feet shoulder-width apart and hold the barbell with an overhand grip, hands slightly closer than shoulder-width apart.\",\"Lift the barbell to shoulder height, keeping your elbows close to your body.\",\"Press the barbell overhead, extending your arms fully.\",\"Lower the barbell back to shoulder height.\",\"Repeat for the desired number of repetitions.\"]', 'en', 7, 3, 7, '2024-02-19 19:17:17', '2024-02-19 19:17:21', 212),
(213, 'barbell standing concentration curl', '213.gif', 'forearms', '[\"Stand with your feet shoulder-width apart and hold a barbell in one hand, palm facing up.\",\"Rest your opposite hand on your thigh for support.\",\"Keeping your upper arm stationary, exhale and curl the weight up towards your shoulder.\",\"Pause for a moment at the top, squeezing your biceps.\",\"Inhale and slowly lower the weight back down to the starting position.\",\"Repeat for the desired number of repetitions, then switch arms.\"]', 'en', 8, 3, 4, '2024-02-19 19:17:21', '2024-02-19 19:17:26', 213),
(214, 'barbell standing front raise over head', '214.gif', 'triceps,upper back', '[\"Stand with your feet shoulder-width apart, holding a barbell in front of your thighs with an overhand grip.\",\"Keep your back straight and engage your core.\",\"Slowly raise the barbell in front of you, keeping your arms straight and your palms facing down.\",\"Continue lifting until the barbell is slightly above shoulder level.\",\"Pause for a moment at the top, then slowly lower the barbell back down to the starting position.\",\"Repeat for the desired number of repetitions.\"]', 'en', 7, 3, 7, '2024-02-19 19:17:26', '2024-02-19 19:17:27', 214),
(215, 'barbell standing leg calf raise', '215.gif', 'hamstrings,glutes', '[\"Stand with your feet shoulder-width apart and place a barbell across your upper back.\",\"Raise your heels off the ground as high as possible, using your calves.\",\"Pause for a moment at the top, then slowly lower your heels back down to the starting position.\",\"Repeat for the desired number of repetitions.\"]', 'en', 5, 3, 5, '2024-02-19 19:17:28', '2024-02-19 19:17:30', 215),
(216, 'barbell standing overhead triceps extension', '216.gif', 'shoulders', '[\"Stand with your feet shoulder-width apart and hold a barbell with an overhand grip.\",\"Raise the barbell overhead, fully extending your arms.\",\"Keeping your upper arms close to your head, slowly lower the barbell behind your head by bending your elbows.\",\"Pause for a moment, then raise the barbell back to the starting position by extending your arms.\",\"Repeat for the desired number of repetitions.\"]', 'en', 8, 3, 18, '2024-02-19 19:17:30', '2024-02-19 19:17:33', 216),
(217, 'barbell standing reverse grip curl', '217.gif', 'forearms', '[\"Stand up straight with your feet shoulder-width apart and hold a barbell with an underhand grip, palms facing up.\",\"Keep your elbows close to your torso and your upper arms stationary.\",\"Exhale and curl the weights while contracting your biceps, bringing the barbell as close to your shoulders as possible.\",\"Hold the contracted position for a brief pause as you squeeze your biceps.\",\"Inhale and slowly lower the barbell back to the starting position.\",\"Repeat for the desired number of repetitions.\"]', 'en', 8, 3, 4, '2024-02-19 19:17:33', '2024-02-19 19:17:36', 217),
(218, 'barbell standing rocking leg calf raise', '218.gif', 'hamstrings,quadriceps', '[\"Stand with your feet shoulder-width apart and hold a barbell across your upper back.\",\"Raise your heels off the ground as high as possible, balancing on the balls of your feet.\",\"Slowly lower your heels back down to the starting position.\",\"Repeat for the desired number of repetitions.\"]', 'en', 5, 3, 5, '2024-02-19 19:17:36', '2024-02-19 19:17:40', 218),
(219, 'barbell standing twist', '219.gif', 'obliques,lower back', '[\"Stand with your feet shoulder-width apart and hold a barbell in front of your chest with both hands, palms facing down.\",\"Engage your core and keep your back straight throughout the exercise.\",\"Slowly twist your torso to the right, pivoting on your feet and hips, while keeping your lower body stable.\",\"Pause for a moment at the end of the twist, then slowly return to the starting position.\",\"Repeat the twist to the left side.\",\"Continue alternating twists for the desired number of repetitions.\"]', 'en', 10, 3, 2, '2024-02-19 19:17:40', '2024-02-19 19:17:44', 219),
(220, 'barbell standing wide grip biceps curl', '220.gif', 'forearms', '[\"Stand with your feet shoulder-width apart and hold a barbell with an underhand grip, hands wider than shoulder-width apart.\",\"Keep your back straight and your elbows close to your torso.\",\"Exhale and curl the barbell up towards your shoulders, keeping your upper arms stationary.\",\"Pause for a moment at the top, squeezing your biceps.\",\"Inhale and slowly lower the barbell back to the starting position, fully extending your arms.\",\"Repeat for the desired number of repetitions.\"]', 'en', 8, 3, 4, '2024-02-19 19:17:44', '2024-02-19 19:17:47', 220),
(221, 'barbell standing wide military press', '221.gif', 'triceps,upper back', '[\"Stand with your feet shoulder-width apart and hold the barbell with an overhand grip, slightly wider than shoulder-width.\",\"Lift the barbell to shoulder height, keeping your elbows slightly in front of the bar.\",\"Press the barbell overhead, extending your arms fully.\",\"Lower the barbell back to shoulder height and repeat for the desired number of repetitions.\"]', 'en', 7, 3, 7, '2024-02-19 19:17:47', '2024-02-19 19:17:51', 221),
(222, 'barbell standing wide-grip curl', '222.gif', 'forearms,shoulders', '[\"Stand up straight with your feet shoulder-width apart and hold a barbell with an overhand grip, hands wider than shoulder-width apart.\",\"Let the barbell hang at arm\'s length in front of your thighs, with your palms facing away from your body.\",\"Keeping your upper arms stationary, exhale and curl the barbell upward by contracting your biceps.\",\"Continue to raise the barbell until your biceps are fully contracted and the barbell is at shoulder level.\",\"Hold the contracted position for a brief pause as you squeeze your biceps.\",\"Inhale and slowly begin to lower the barbell back to the starting position.\",\"Repeat for the desired number of repetitions.\"]', 'en', 8, 3, 4, '2024-02-19 19:17:51', '2024-02-19 19:17:53', 222),
(223, 'barbell step-up', '223.gif', 'quadriceps,hamstrings,calves', '[\"Stand in front of a bench or step with a barbell resting on your upper back.\",\"Place one foot on the bench or step, ensuring your entire foot is in contact with the surface.\",\"Push through your heel and step up onto the bench or step, fully extending your hip and knee.\",\"Pause briefly at the top, then lower yourself back down to the starting position.\",\"Repeat with the opposite leg.\",\"Continue alternating legs for the desired number of repetitions.\"]', 'en', 9, 3, 9, '2024-02-19 19:17:53', '2024-02-19 19:17:56', 223),
(224, 'barbell stiff leg good morning', '224.gif', 'hamstrings,lower back', '[\"Stand with your feet shoulder-width apart and your knees slightly bent.\",\"Hold the barbell across your upper back, resting it on your traps.\",\"Keeping your back straight, hinge forward at the hips, pushing your glutes back.\",\"Lower your torso until it is parallel to the ground, feeling a stretch in your hamstrings.\",\"Engage your glutes and hamstrings to return to the starting position.\",\"Repeat for the desired number of repetitions.\"]', 'en', 9, 3, 9, '2024-02-19 19:17:56', '2024-02-19 19:17:58', 224),
(225, 'barbell straight leg deadlift', '225.gif', 'glutes,lower back', '[\"Stand with your feet shoulder-width apart and your toes pointing forward.\",\"Hold the barbell with an overhand grip, hands slightly wider than shoulder-width apart.\",\"Bend at your hips and lower the barbell towards the ground, keeping your back straight and your knees slightly bent.\",\"Lower the barbell until you feel a stretch in your hamstrings.\",\"Engage your hamstrings and glutes to lift the barbell back up to the starting position.\",\"Repeat for the desired number of repetitions.\"]', 'en', 9, 3, 10, '2024-02-19 19:17:58', '2024-02-19 19:18:01', 225),
(226, 'barbell sumo deadlift', '', 'hamstrings,quadriceps,lower back', '[\"Stand with your feet wider than shoulder-width apart, toes pointing outwards.\",\"Place a barbell on the ground in front of you, centered between your feet.\",\"Bend your knees and lower your hips, keeping your back straight and chest up, to grip the barbell with an overhand grip.\",\"Engage your core and drive through your heels to lift the barbell off the ground, extending your hips and knees simultaneously.\",\"As you lift, keep your chest up and back straight, and push your hips forward to fully engage your glutes.\",\"Pause for a moment at the top, then slowly lower the barbell back down to the starting position, maintaining control throughout the movement.\",\"Repeat for the desired number of repetitions.\"]', 'en', 9, 3, 9, '2024-02-19 19:18:01', '2024-02-19 19:18:01', 0);
